import{_ as e,c as s,o as n,a}from"./app.e867883c.js";var o="/assets/graph.8f2f36b7.png";const h='{"title":"\uC9C0\uC6D0\uD558\uB294 \uAE30\uB2A5\uB4E4","description":"","frontmatter":{"title":"\uC9C0\uC6D0\uD558\uB294 \uAE30\uB2A5\uB4E4"},"headers":[{"level":2,"title":"NPM\uC744 \uC774\uC6A9\uD55C \uB514\uD39C\uB358\uC2DC import \uADF8\uB9AC\uACE0 \uC0AC\uC804 \uBC88\uB4E4\uB9C1","slug":"npm-dependency-resolving-and-pre-building"},{"level":2,"title":"Hot Module Replacement","slug":"hot-module-replacement"},{"level":2,"title":"TypeScript","slug":"typescript"},{"level":3,"title":"\uD0C0\uC785\uC2A4\uD06C\uB9BD\uD2B8 \uCEF4\uD30C\uC77C\uB7EC \uC635\uC158","slug":"typescript-compiler-options"},{"level":3,"title":"Client Types","slug":"client-types"},{"level":2,"title":"Vue","slug":"vue"},{"level":2,"title":"JSX","slug":"jsx"},{"level":2,"title":"CSS","slug":"css"},{"level":3,"title":"CSS @import \uADF8\uB9AC\uACE0 URL \uC7AC\uC815\uC758(Rebasing)","slug":"import-inlining-and-rebasing"},{"level":3,"title":"PostCSS","slug":"postcss"},{"level":3,"title":"CSS Modules","slug":"css-modules"},{"level":3,"title":"CSS Pre-processors","slug":"css-pre-processors"},{"level":2,"title":"\uC815\uC801 \uC5D0\uC14B","slug":"static-assets"},{"level":2,"title":"JSON","slug":"json"},{"level":2,"title":"Glob Import","slug":"glob-import"},{"level":2,"title":"WebAssembly","slug":"web-assembly"},{"level":2,"title":"Web Workers","slug":"web-workers"},{"level":2,"title":"\uBE4C\uB4DC \uCD5C\uC801\uD654","slug":"build-optimizations"},{"level":3,"title":"CSS \uCF54\uB4DC \uBD84\uB9AC","slug":"css-code-splitting"},{"level":3,"title":"Preload Directives Generation","slug":"preload-directives-generation"},{"level":3,"title":"Async Chunk Loading Optimization","slug":"async-chunk-loading-optimization"}],"relativePath":"guide/features.md","lastUpdated":1644060470672}',t={},p=a(`<h1 id="features" tabindex="-1">\uC9C0\uC6D0\uD558\uB294 \uAE30\uB2A5\uB4E4 <a class="header-anchor" href="#features" aria-hidden="true">#</a></h1><p>\uAE30\uBCF8\uC801\uC73C\uB85C vite\uB294 \uC5EC\uD0C0 \uC815\uC801 \uD30C\uC77C \uC11C\uBC84\uC640 \uD06C\uAC8C \uB2E4\uB974\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB2E4\uB9CC, vite\uB294 \uB124\uC774\uD2F0\uBE0C ESM \uB9D0\uACE0\uB3C4 \uAE30\uC874 \uBC88\uB4E4\uB7EC\uC5D0\uC11C \uC81C\uACF5\uD558\uB358 \uAE30\uB2A5\uC744 \uB300\uBD80\uBD84 \uC9C0\uC6D0\uD55C\uB2E4\uB294 \uCC28\uC774\uC810\uC774 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="npm-dependency-resolving-and-pre-building" tabindex="-1">NPM\uC744 \uC774\uC6A9\uD55C \uB514\uD39C\uB358\uC2DC <code>import</code> \uADF8\uB9AC\uACE0 \uC0AC\uC804 \uBC88\uB4E4\uB9C1 <a class="header-anchor" href="#npm-dependency-resolving-and-pre-building" aria-hidden="true">#</a></h2><p>\uB2E4\uC74C \uCF54\uB4DC\uB294 \uB124\uC774\uD2F0\uBE0C ES\uC5D0\uC11C \uC815\uC0C1\uC801\uC73C\uB85C \uC2E4\uD589\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4:</p><div class="language-js"><pre><code><span class="token keyword">import</span> <span class="token punctuation">{</span> someMethod <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;my-dep&#39;</span>
</code></pre></div><p>\uBAA8\uB4C8\uC758 \uC704\uCE58\uB97C \uCC3E\uC744 \uC218 \uC5C6\uAE30 \uB54C\uBB38\uC778\uB370, vite\uB294 \uB2E4\uC74C\uC744 \uAE30\uC900\uC73C\uB85C \uBAA8\uB4C8\uC744 \uAC00\uC838\uC624\uAE30 \uB54C\uBB38\uC5D0 \uC704 \uCF54\uB4DC \uC5ED\uC2DC \uC815\uC0C1\uC801\uC73C\uB85C \uC2E4\uD589\uB429\uB2C8\uB2E4.</p><ol><li><p>Vite\uB97C \uD1B5\uD574 ESM \uC2A4\uD0C0\uC77C\uB85C <a href="./dep-pre-bundling.html">\uC0AC\uC804\uC5D0 \uBC88\uB4E4\uB9C1 \uB41C</a> CommonJS \uBC0F UMD* \uBAA8\uB4C8. \uC774 \uACFC\uC815\uC740 <a href="https://esbuild.github.io/" target="_blank" rel="noopener noreferrer">Esbuild</a>\uB97C \uD1B5\uD574 \uC774\uB8E8\uC5B4\uC9C0\uBA70, JavaScript \uAE30\uBC18\uC758 \uB2E4\uB978 \uBC88\uB4E4\uB7EC\uBCF4\uB2E4 \uBE60\uB978 Cold-starting\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4. (* Universal Module Definition: CommonJS\uC640 AMD \uC2A4\uD0C0\uC77C\uC758 \uBAA8\uB4C8\uC744 \uB458 \uB2E4 \uC9C0\uC6D0\uD558\uB294 \uBAA8\uB4C8 \uD615\uD0DC)</p></li><li><p><code>/node_modules/.vite/my-dep.js?v=f3sf2ebd</code>\uC640 \uAC19\uC774 URL\uC744 \uC774\uC6A9\uD574 ESM\uC744 \uC9C0\uC6D0\uD558\uB294 \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uBAA8\uB4C8\uC744 \uAC00\uC838\uC62C \uC218 \uC788\uB3C4\uB85D <code>import</code> \uAD6C\uBB38\uC744 \uC218\uC815.</p></li></ol><p>\uCC38\uACE0\uB85C, <strong>\uB514\uD39C\uB358\uC2DC\uB294 \uBC18\uB4DC\uC2DC \uCE90\uC2DC\uB429\uB2C8\uB2E4.</strong></p><p>vite\uB294 HTTP \uD5E4\uB354\uB97C \uC774\uC6A9\uD574 \uC694\uCCAD\uD55C \uB514\uD39C\uB358\uC2DC\uB97C \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uCE90\uC2F1\uD558\uB3C4\uB85D \uD569\uB2C8\uB2E4. \uB9CC\uC57D \uB514\uD39C\uB358\uC2DC\uC758 \uC218\uC815 \uB610\uB294 \uB514\uBC84\uAE45\uC774 \uD544\uC694\uD558\uB2E4\uBA74 <a href="./dep-pre-bundling.html#browser-cache">\uC5EC\uAE30</a>\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><h2 id="hot-module-replacement" tabindex="-1">Hot Module Replacement <a class="header-anchor" href="#hot-module-replacement" aria-hidden="true">#</a></h2><p>vite\uB294 \uC774\uC6A9\uD574 \uB124\uC774\uD2F0\uBE0C ESM \uAE30\uBC18\uC758 <a href="./api-hmr.html">HMR API</a>\uC744 \uC81C\uACF5\uD569\uB2C8\uB2E4. HMR\uC744 \uC774\uC6A9\uD558\uB294 \uD504\uB808\uC784\uC6CC\uD06C\uB294 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 \uB2E4\uC2DC \uC2DC\uC791\uD558\uC9C0 \uC54A\uACE0\uB3C4 \uC77C\uBD80 \uCEE8\uD150\uCE20\uB9CC\uC744 \uAC31\uC2E0\uD560 \uC218 \uC788\uB2E4\uB294 \uC7A5\uC810\uC774 \uC788\uB294\uB370, vite\uB294 <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-vue" target="_blank" rel="noopener noreferrer">Vue Single File Components</a>, <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-react" target="_blank" rel="noopener noreferrer">React Fast Refresh</a> \uB610\uB294 <a href="https://github.com/JoviDeCroock/prefresh/tree/main/packages/vite" target="_blank" rel="noopener noreferrer">@prefresh/vite</a>\uACFC \uAC19\uC740 First-party HMR* \uBAA8\uB4C8\uC744 \uC81C\uACF5\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. (* Vite\uC5D0\uC11C \uC9C1\uC811 \uC81C\uACF5\uD558\uB294 HMR \uBAA8\uB4C8)</p><p>\uBB3C\uB860, <a href="./"><code>create-vite</code></a>\uC5D0\uC11C \uC81C\uACF5\uD558\uB294 \uD15C\uD50C\uB9BF \uC548\uC5D0\uB294 HMR \uBAA8\uB4C8\uC774 \uD3EC\uD568\uB418\uC5B4 \uC788\uAE30 \uB54C\uBB38\uC5D0 \uAD73\uC774 \uC704\uC640 \uAC19\uC740 \uBC29\uBC95\uC744 \uB530\uB974\uC9C0 \uC54A\uC544\uB3C4 \uB429\uB2C8\uB2E4.</p><h2 id="typescript" tabindex="-1">TypeScript <a class="header-anchor" href="#typescript" aria-hidden="true">#</a></h2><p>vite\uB294 <code>.ts</code> \uD30C\uC77C\uC5D0 \uB300\uD55C \uCEF4\uD30C\uC77C\uB9C1 \uBC0F Import \uC5ED\uC2DC \uC9C0\uC6D0\uD569\uB2C8\uB2E4.</p><p>\uB2E8, \uD0C0\uC785 \uCCB4\uD0B9\uC740 \uC624\uB85C\uC9C0 IDE \uB610\uB294 \uBE4C\uB4DC \uD504\uB85C\uC138\uC2A4\uC5D0\uB9CC \uC758\uC874\uD558\uBA70, Vite \uC790\uCCB4\uC5D0\uC11C\uB294 <code>.ts</code> \uD30C\uC77C\uC5D0 \uB300\uD55C <strong>\uD0C0\uC785 \uCCB4\uD0B9 \uC791\uC5C5\uC744 \uC9C4\uD589\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4</strong>. \uD0C0\uC785 \uCCB4\uD0B9\uC774 \uD544\uC694\uD558\uB2E4\uBA74 \uD544\uC694\uD558\uB2E4\uBA74 <code>tsc --noEmit</code>*\uC744 \uBE4C\uB4DC \uC2A4\uD06C\uB9BD\uD2B8\uC5D0 \uB123\uC5B4\uC8FC\uC138\uC694. \uB9CC\uC57D <code>*.vue</code> \uC18C\uC2A4 \uCF54\uB4DC\uB97C \uC791\uC131\uC911\uC774\uB77C\uBA74, <code>vue-tsc</code>\uB97C \uC124\uCE58\uD574 <code>vue-tsc --noEmit</code>\uC744 \uBE4C\uB4DC \uC2A4\uD06C\uB9BD\uD2B8\uC5D0 \uB123\uC5B4\uC11C \uD0C0\uC785 \uCCB4\uD0B9\uC744 \uD558\uB3C4\uB85D \uC124\uC815\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. (* <code>--noEmit</code>: \uCEF4\uD30C\uC77C\uB9C1 \uC5C6\uC774 \uD0C0\uC785 \uCCB4\uD0B9\uB9CC\uC744 \uC218\uD589\uD558\uB294 \uC635\uC158)</p><p>Vite\uC758 TypeScript \uCEF4\uD30C\uC77C\uB9C1\uC740 <a href="https://github.com/evanw/esbuild" target="_blank" rel="noopener noreferrer">Esbuild</a>\uB97C \uC774\uC6A9\uD558\uBA70, TypeScript \uC18C\uC2A4 \uCF54\uB4DC\uB97C JavaScript \uC18C\uC2A4 \uCF54\uB4DC\uB85C \uBCC0\uD658\uD558\uB294 \uC791\uC5C5\uC5D0 \uB300\uD574 <code>tsc</code> \uB300\uBE44 \uC57D 20~30\uBC30 \uC815\uB3C4 \uBE60\uB978 \uD37C\uD3EC\uBA3C\uC2A4\uB97C \uBCF4\uC774\uACE0 \uC788\uC2B5\uB2C8\uB2E4. (HMR\uC740 50ms \uBBF8\uB9CC)</p><p>\uCC38\uACE0\uB85C \uD0C0\uC785\uB9CC\uC744 \uAC00\uC838\uC624\uB294 \uACBD\uC6B0 \uC798\uBABB \uBC88\uB4E4\uB9C1\uC774 \uB420 \uC218 \uC788\uC73C\uBA70, \uC774\uB294 <a href="https://www.typescriptlang.org/ko/docs/handbook/release-notes/typescript-3-8.html#type-only-imports-and-export" target="_blank" rel="noopener noreferrer">\uD0C0\uC785 \uC804\uC6A9 Imports\uC640 Exports</a>\uB97C \uC0AC\uC6A9\uD558\uC5EC \uC774 \uBB38\uC81C\uB97C \uC6B0\uD68C\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4:</p><div class="language-ts"><pre><code><span class="token keyword">import</span> <span class="token keyword">type</span> <span class="token punctuation">{</span> <span class="token constant">T</span> <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;only/types&#39;</span>
<span class="token keyword">export</span> <span class="token keyword">type</span> <span class="token punctuation">{</span> <span class="token constant">T</span> <span class="token punctuation">}</span>
</code></pre></div><h3 id="typescript-compiler-options" tabindex="-1">\uD0C0\uC785\uC2A4\uD06C\uB9BD\uD2B8 \uCEF4\uD30C\uC77C\uB7EC \uC635\uC158 <a class="header-anchor" href="#typescript-compiler-options" aria-hidden="true">#</a></h3><p><code>tsconfig.json</code> \uD30C\uC77C \uB0B4 <code>compilerOptions</code> \uC124\uC815\uB4E4\uC758 \uAC12\uC744 \uC870\uC791\uD560 \uB54C\uB294 \uD2B9\uBCC4\uD55C \uC8FC\uC758\uAC00 \uD544\uC694\uD569\uB2C8\uB2E4.</p><h4 id="isolatedmodules" tabindex="-1"><code>isolatedModules</code> <a class="header-anchor" href="#isolatedmodules" aria-hidden="true">#</a></h4><p><code>true</code>\uB85C \uC124\uC815\uD574\uC57C \uD569\uB2C8\uB2E4.</p><p><code>esbuild</code>\uB294 \uD0C0\uC785\uC5D0 \uB300\uD55C \uC815\uBCF4 \uC5C6\uC774 \uBCC0\uD658(Transpilation)\uB9CC\uC744 \uC218\uD589\uD558\uAE30\uC5D0, const enum \uB610\uB294 \uC554\uC2DC\uC801\uC73C\uB85C \uD0C0\uC785\uB9CC\uC744 \uAC00\uC838\uC624\uB294 \uAC83\uACFC \uAC19\uC740 \uD2B9\uC815 \uAE30\uB2A5\uC744 \uC9C0\uC6D0\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</p><p>\uC774\uB97C \uAC10\uC9C0\uD558\uAE30 \uC704\uD574 <code>tsconfig.json</code> \uB0B4 <code>compilerOptions</code> \uC124\uC815\uC744 <code>&quot;isolatedModules&quot;: true</code>\uC640 \uAC19\uC774 \uC124\uC815\uD574\uC918\uC57C\uB9CC \uD558\uBA70, \uC774 \uC124\uC815\uC73C\uB85C TS\uAC00 \uC704\uC640 \uAC19\uC740 \uC0C1\uD669\uC5D0\uC11C \uC791\uB3D9\uD558\uC9C0 \uC54A\uB294 \uAE30\uB2A5\uB4E4\uC5D0 \uB300\uD574 \uACBD\uACE0\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4.</p><h4 id="usedefineforclassfields" tabindex="-1"><code>useDefineForClassFields</code> <a class="header-anchor" href="#usedefineforclassfields" aria-hidden="true">#</a></h4><p>Vite 2.5.0 \uBD80\uD130\uB294 TypeScript\uC758 \uBCC0\uD658 \uB300\uC0C1\uC774 <code>ESNext</code>\uC778 \uACBD\uC6B0, \uAE30\uBCF8 \uAC12\uC744 <code>true</code>\uB85C \uC124\uC815\uD569\uB2C8\uB2E4. \uC774\uB294 <a href="https://github.com/microsoft/TypeScript/pull/42663" target="_blank" rel="noopener noreferrer"><code>tsc</code> \uBC84\uC804 4.3.2 \uC774\uC0C1</a> \uBC0F ECMAScript \uD45C\uC900\uC744 \uB530\uB974\uB3C4\uB85D \uD558\uB294 \uC124\uC815\uC785\uB2C8\uB2E4.</p><p>\uADF8\uB7EC\uB098 \uB2E4\uB978 \uD504\uB85C\uADF8\uB798\uBC0D \uC5B8\uC5B4\uB098 \uC774\uC804 \uBC84\uC804\uC758 TypeScript\uB97C \uC0AC\uC6A9\uD558\uB358 \uC0AC\uB78C\uB4E4\uC5D0\uAC8C\uB294 \uC9C1\uAD00\uC801\uC774\uC9C0 \uC54A\uC740 \uB0B4\uC6A9\uC77C \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uC5D0 \uB300\uD55C \uC790\uC138\uD55C \uC815\uBCF4\uB294 <a href="https://www.typescriptlang.org/docs/handbook/release-notes/typescript-3-7.html#the-usedefineforclassfields-flag-and-the-declare-property-modifier" target="_blank" rel="noopener noreferrer">TypeScript 3.7 \uB9B4\uB9AC\uC2A4 \uB178\uD2B8</a>\uB97C \uCC38\uACE0\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uB610\uD55C, <a href="https://mobx.js.org/installation.html#use-spec-compliant-transpilation-for-class-properties" target="_blank" rel="noopener noreferrer">MobX</a>\uB098 <a href="https://github.com/vuejs/vue-class-component/issues/465" target="_blank" rel="noopener noreferrer">Vue \uD074\uB798\uC2A4 \uCEF4\uD3EC\uB10C\uD2B8 8.X</a>\uC640 \uAC19\uC740 \uB300\uBD80\uBD84\uC758 \uB77C\uC774\uBE0C\uB7EC\uB9AC\uB294 <code>&quot;useDefineForClassFields&quot;: true</code>\uC778 \uAC83\uC73C\uB85C \uAC00\uC815\uD558\uACE0 \uB3D9\uC791\uD558\uBE48\uB2E4. \uB530\uB77C\uC11C \uB9CC\uC57D \uD074\uB798\uC2A4\uC758 \uD544\uB4DC\uC5D0 \uD06C\uAC8C \uC758\uC874\uD558\uB294 \uB77C\uC774\uBE0C\uB7EC\uB9AC\uB97C \uC0AC\uC6A9\uD558\uB294 \uACBD\uC6B0\uB77C\uBA74, \uC774\uB7EC\uD55C \uB77C\uC774\uBE0C\uB7EC\uB9AC\uB97C \uC0AC\uC6A9\uD558\uB294 \uAC83\uC5D0 \uB300\uD574 \uC635\uC158\uC744 \uC218\uC815\uD558\uB294 \uACBD\uC6B0 \uC0C1\uB2F9\uD55C \uC8FC\uC758\uB97C \uAE30\uC6B8\uC5B4\uC57C \uD569\uB2C8\uB2E4.</p><p>\uB2E4\uB9CC <a href="https://github.com/lit/lit-element/issues/1030" target="_blank" rel="noopener noreferrer"><code>lit-element</code></a>\uB97C \uD3EC\uD568\uD574 \uC77C\uBD80 \uB77C\uC774\uBE0C\uB7EC\uB9AC\uB294 \uC544\uC9C1 \uC774 \uC0C8\uB85C\uC6B4 \uAE30\uBCF8\uAC12\uC774 \uC801\uC6A9\uB418\uC9C0 \uC54A\uC558\uC2B5\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uACBD\uC6B0\uC5D0\uB294 <code>useDefineForClassFields</code>\uC758 \uAC12\uC744 <code>false</code>\uB85C \uC124\uC815\uD574\uC8FC\uC138\uC694.</p><h4 id="other-compiler-options-affecting-the-build-result" tabindex="-1">\uBE4C\uB4DC \uACB0\uACFC\uC5D0 \uC601\uD5A5\uC744 \uBBF8\uCE58\uB294 \uB610 \uB2E4\uB978 \uCEF4\uD30C\uC77C\uB7EC \uC635\uC158\uB4E4 <a class="header-anchor" href="#other-compiler-options-affecting-the-build-result" aria-hidden="true">#</a></h4><ul><li><a href="https://www.typescriptlang.org/tsconfig#extends" target="_blank" rel="noopener noreferrer"><code>extends</code></a></li><li><a href="https://www.typescriptlang.org/tsconfig#importsNotUsedAsValues" target="_blank" rel="noopener noreferrer"><code>importsNotUsedAsValues</code></a></li><li><a href="https://www.typescriptlang.org/tsconfig#preserveValueImports" target="_blank" rel="noopener noreferrer"><code>preserveValueImports</code></a></li><li><a href="https://www.typescriptlang.org/tsconfig#jsxFactory" target="_blank" rel="noopener noreferrer"><code>jsxFactory</code></a></li><li><a href="https://www.typescriptlang.org/tsconfig#jsxFragmentFactory" target="_blank" rel="noopener noreferrer"><code>jsxFragmentFactory</code></a></li></ul><p>\uB2E4\uB9CC <code>&quot;isolatedModules&quot;: true</code>\uB85C \uB9C8\uC774\uADF8\uB808\uC774\uC158 \uD560 \uC218 \uC5C6\uB2E4\uBA74, <a href="https://www.npmjs.com/package/rollup-plugin-friendly-type-imports" target="_blank" rel="noopener noreferrer">rollup-plugin-friendly-type-imports</a>\uC640 \uAC19\uC740 \uC368\uB4DC \uD30C\uD2F0 \uD50C\uB7EC\uADF8\uC778\uC744 \uC0AC\uC6A9\uD558\uB294 \uBC29\uBC95\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uADF8\uB7EC\uB098 \uC774\uB7EC\uD55C \uC811\uADFC \uBC29\uC2DD\uC740 Vite\uC5D0\uC11C \uACF5\uC2DD\uC801\uC73C\uB85C \uC9C0\uC6D0\uD558\uB294 \uBC29\uBC95\uC740 \uC544\uB2D9\uB2C8\uB2E4.</p><h3 id="client-types" tabindex="-1">Client Types <a class="header-anchor" href="#client-types" aria-hidden="true">#</a></h3><p>vite\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C Node.js API \uAE30\uBC18\uC758 \uD0C0\uC785 \uC2DC\uC2A4\uD15C\uC744 \uCC28\uC6A9\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C Client-side\uC758 \uD658\uACBD\uC744 \uC704\uD574 Shim\uC744 \uAD6C\uC131\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>d.ts</code> \uC120\uC5B8 \uD30C\uC77C\uC744 \uCD94\uAC00\uD574\uC8FC\uC138\uC694.</p><div class="language-typescript"><pre><code><span class="token comment">/// &lt;reference types=&quot;vite/client&quot; /&gt;</span>
</code></pre></div><p>\uB610\uB294, <code>tsconfig</code> \uB0B4 <code>compilerOptions.types</code> \uC635\uC158\uC5D0 <code>vite/client</code>\uB97C \uBA85\uC2DC\uD574 \uC904 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uC774\uB97C \uD1B5\uD574 \uB2E4\uC74C\uC5D0 \uB300\uD55C Shim\uC774 \uC81C\uACF5\uB429\uB2C8\uB2E4.</p><ul><li><code>.svg</code>\uC640 \uAC19\uC740 \uC5D0\uC14B</li><li>Vite\uB97C \uD1B5\uD574 \uC8FC\uC785\uB418\uB294 <code>import.<wbr>meta.env</code>\uC5D0 \uBA85\uC2DC\uB41C <a href="./env-and-mode.html#env-variables">\uD658\uACBD \uBCC0\uC218</a> \uD0C0\uC785\uB4E4</li><li><code>import.<wbr>meta.hot</code>\uC5D0 \uBA85\uC2DC\uB41C <a href="./api-hmr.html">HMR API</a> \uD0C0\uC785\uB4E4</li></ul><h2 id="vue" tabindex="-1">Vue <a class="header-anchor" href="#vue" aria-hidden="true">#</a></h2><p>vite\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C Vue\uB97C \uC9C0\uC6D0\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li>Vue 3 SFC : <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-vue" target="_blank" rel="noopener noreferrer">@vitejs/plugin-vue</a></li><li>Vue 3 JSX : <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-vue-jsx" target="_blank" rel="noopener noreferrer">@vitejs/plugin-vue-jsx</a></li><li>Vue 2 : <a href="https://github.com/underfin/vite-plugin-vue2" target="_blank" rel="noopener noreferrer">underfin/vite-plugin-vue2</a></li></ul><h2 id="jsx" tabindex="-1">JSX <a class="header-anchor" href="#jsx" aria-hidden="true">#</a></h2><p><code>.jsx</code>\uC640 <code>.tsx</code> \uC5ED\uC2DC \uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4. \uB9C8\uCC2C\uAC00\uC9C0\uB85C <a href="https://esbuild.github.io" target="_blank" rel="noopener noreferrer">ESBuild</a>\uB97C \uC774\uC6A9\uD574 \uCEF4\uD30C\uC77C\uB9C1\uD558\uBA70, React 16 \uC2A4\uD0C0\uC77C\uC758 JSX\uB97C \uAE30\uBCF8\uC801\uC73C\uB85C \uC9C0\uC6D0\uD569\uB2C8\uB2E4. React 17 \uC2A4\uD0C0\uC77C\uC758 JSX\uB294 <a href="https://github.com/evanw/esbuild/issues/334" target="_blank" rel="noopener noreferrer">\uD604\uC7AC \uC791\uC5C5 \uC911\uC5D0 \uC788\uC2B5\uB2C8\uB2E4</a>.</p><p>\uAE30\uC874\uC5D0 Vue\uB97C \uC0AC\uC6A9\uD588\uB358 \uAC1C\uBC1C\uC790\uB4E4\uC740 Vue 3\uC5D0\uC11C \uC81C\uACF5\uD558\uACE0 \uC788\uB294 API(HMR, \uAE00\uB85C\uBC8C \uCEF4\uD3EC\uB10C\uD2B8, \uB514\uB809\uD2F0\uBE0C \uBC0F \uC2AC\uB86F \uB4F1)\uB97C \uC704\uD574 <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-vue-jsx" target="_blank" rel="noopener noreferrer">@vitejs/plugin-vue-jsx</a>\uB97C \uC0AC\uC6A9\uD574\uC57C \uD569\uB2C8\uB2E4.</p><p>\uBB3C\uB860 React\uB098 Vue\uB97C \uC0AC\uC6A9\uD558\uC9C0 \uC54A\uB294\uB2E4 \uD574\uB3C4, <a href="/config/#esbuild"><code>esbuild</code> \uC635\uC158</a>\uC744 \uC774\uC6A9\uD574 <code>jsxFactory</code>\uB098 <code>jsxFragment</code>\uB97C \uCEE4\uC2A4\uD130\uB9C8\uC774\uC9D5 \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. Preact\uB97C \uC608\uB85C \uB4E4\uC5B4\uBCF4\uC790\uBA74 \uB2E4\uC74C\uACFC \uAC19\uC2B5\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// vite.config.js</span>
<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token function">defineConfig</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">esbuild</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token literal-property property">jsxFactory</span><span class="token operator">:</span> <span class="token string">&#39;h&#39;</span><span class="token punctuation">,</span>
    <span class="token literal-property property">jsxFragment</span><span class="token operator">:</span> <span class="token string">&#39;Fragment&#39;</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\uC790\uC138\uD55C \uC0AC\uD56D\uC740 <a href="https://esbuild.github.io/content-types/#jsx" target="_blank" rel="noopener noreferrer">esbuild \uBB38\uC11C</a>\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><p>\uCC38\uACE0\uB85C, Vite\uC5D0\uC11C\uB9CC \uC81C\uACF5\uB418\uB294 \uC635\uC158\uC778 <code>jsxInject</code>\uB97C \uC774\uC6A9\uD574 JSX\uC5D0 \uB300\uD55C \uD5EC\uD37C\uB97C \uC0AC\uC6A9\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// vite.config.js</span>
<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token function">defineConfig</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">esbuild</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token literal-property property">jsxInject</span><span class="token operator">:</span> <span class="token template-string"><span class="token template-punctuation string">\`</span><span class="token string">import React from &#39;react&#39;</span><span class="token template-punctuation string">\`</span></span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><h2 id="css" tabindex="-1">CSS <a class="header-anchor" href="#css" aria-hidden="true">#</a></h2><p><code>.css</code> \uD30C\uC77C\uC744 Import \uD560 \uB54C, \uAE30\uBCF8\uC801\uC73C\uB85C HMR\uC744 \uC704\uD574 <code>&lt;style&gt;</code> \uD0DC\uADF8\uB85C \uBCC0\uD658\uB418\uC5B4 \uBD88\uB7EC\uC640\uC9C0\uAC8C \uB429\uB2C8\uB2E4. \uBB3C\uB860 String \uD0C0\uC785\uC758 \uBAA8\uB4C8\uB85C \uAC00\uC838\uC62C \uC218\uB3C4 \uC788\uAD6C\uC694.</p><div class="language-js"><pre><code><span class="token keyword">import</span> style <span class="token keyword">from</span> <span class="token string">&#39;./style.css&#39;</span>

console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>style<span class="token punctuation">)</span> <span class="token comment">// \uC815\uC758\uD55C CSS\uB97C \uBB38\uC790\uC5F4\uB85C \uAC00\uC838\uC635\uB2C8\uB2E4.</span>
</code></pre></div><h3 id="import-inlining-and-rebasing" tabindex="-1">CSS <code>@import</code> \uADF8\uB9AC\uACE0 URL \uC7AC\uC815\uC758(Rebasing) <a class="header-anchor" href="#import-inlining-and-rebasing" aria-hidden="true">#</a></h3><p>vite\uB294 <code>postcss-import</code>\uB97C \uC774\uC6A9\uD574 CSS\uC758 <code>@import</code>\uB97C \uCC98\uB9AC\uD569\uB2C8\uB2E4. \uB610\uD55C, CSS <code>url()</code>\uB85C \uCC38\uC870\uB418\uB294 \uBAA8\uB4E0 \uB9AC\uC18C\uC2A4\uB4E4(\uB2E4\uB978 \uB514\uB809\uD130\uB9AC\uC5D0 \uC874\uC7AC\uD55C\uB2E4 \uD574\uB3C4)\uC5D0 \uB300\uD574 \uBCC4\uB2E4\uB978 \uC124\uC815 \uC5C6\uC774 \uC790\uB3D9\uC73C\uB85C Base\uB97C \uB9DE\uCD94\uC5B4\uC8FC\uB294 \uC7AC\uC815\uC758(Rebasing) \uC791\uC5C5 \uC5ED\uC2DC \uC9C4\uD589\uD574\uC8FC\uACE0 \uB9D0\uC774\uC8E0.</p><p>\uBCC4\uCE6D\uC744 \uC774\uC6A9\uD55C <code>@import</code>\uB3C4 \uC9C0\uC6D0\uD558\uBA70, URL \uC7AC\uC815\uC758\uB098 \uBCC4\uCE6D\uC740 CSS \uB9D0\uACE0\uB3C4 Sass\uC640 Less\uC5D0\uC11C\uB3C4 <a href="#css-pre-processors">\uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4</a>.</p><h3 id="postcss" tabindex="-1">PostCSS <a class="header-anchor" href="#postcss" aria-hidden="true">#</a></h3><p>\uB9CC\uC57D \uD504\uB85C\uC81D\uD2B8\uC5D0 <a href="https://github.com/postcss/postcss-load-config" target="_blank" rel="noopener noreferrer">PostCSS \uC124\uC815 \uD30C\uC77C</a>\uC774 \uC874\uC7AC\uD55C\uB2E4\uBA74, vite\uB294 \uC774\uB97C \uC774\uC6A9\uD574 \uBAA8\uB4E0 CSS \uD30C\uC77C\uC5D0 \uD574\uB2F9 \uC124\uC815\uC744 \uC801\uC6A9\uD569\uB2C8\uB2E4.</p><h3 id="css-modules" tabindex="-1">CSS Modules <a class="header-anchor" href="#css-modules" aria-hidden="true">#</a></h3><p><code>.module.css</code> \uD655\uC7A5\uC790\uB85C \uB05D\uB098\uB294 \uBAA8\uB4E0 CSS \uD30C\uC77C\uB4E4\uC740 <a href="https://github.com/css-modules/css-modules" target="_blank" rel="noopener noreferrer">CSS \uBAA8\uB4C8 \uD30C\uC77C</a>\uB85C \uCDE8\uAE09\uB418\uBA70, \uC77C\uBC18\uC801\uC778 JavaScript \uBAA8\uB4C8\uCC98\uB7FC \uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p><div class="language-css"><pre><code><span class="token comment">/* example.module.css */</span>
<span class="token selector">.red</span> <span class="token punctuation">{</span>
  <span class="token property">color</span><span class="token punctuation">:</span> red<span class="token punctuation">;</span>
<span class="token punctuation">}</span>
</code></pre></div><div class="language-js"><pre><code><span class="token keyword">import</span> classes <span class="token keyword">from</span> <span class="token string">&#39;./example.module.css&#39;</span>
document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">&#39;foo&#39;</span><span class="token punctuation">)</span><span class="token punctuation">.</span>className <span class="token operator">=</span> classes<span class="token punctuation">.</span>red
</code></pre></div><p>\uCC38\uACE0\uB85C, CSS \uBAA8\uB4C8\uC5D0 \uB300\uD55C \uB3D9\uC791 \uBC29\uC2DD\uC744 \uC785\uB9DB\uB300\uB85C \uC218\uC815\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. <a href="/config/#css-modules"><code>css.modules</code> \uC635\uC158</a> \uC635\uC158\uC744 \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><p>\uAC00\uB839 <code>css.modules.localsConvention</code> \uC635\uC158\uC744 camelCase\uB85C \uC124\uC815\uD558\uAC8C \uB418\uBA74(<code>localsConvention: &#39;camelCaseOnly&#39;</code>), \uC544\uB798\uC640 \uAC19\uC774 \uAC00\uC838\uC62C \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// .apply-color -&gt; applyColor</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> applyColor <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;./example.module.css&#39;</span>
document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">&#39;foo&#39;</span><span class="token punctuation">)</span><span class="token punctuation">.</span>className <span class="token operator">=</span> applyColor
</code></pre></div><h3 id="css-pre-processors" tabindex="-1">CSS Pre-processors <a class="header-anchor" href="#css-pre-processors" aria-hidden="true">#</a></h3><p>vite\uB294 \uBAA8\uB358 \uBE0C\uB77C\uC6B0\uC800\uB9CC\uC744 \uD0C0\uAE43\uC73C\uB85C \uD558\uAE30\uC5D0, \uD45C\uC900\uC744 \uC900\uC218\uD558\uB3C4\uB85D <a href="https://github.com/jonathantneal/postcss-nesting" target="_blank" rel="noopener noreferrer">postcss-nesting</a>\uACFC \uAC19\uC740 CSSWG \uCD08\uC548\uC744 \uAD6C\uD604\uD55C PostCSS \uD50C\uB7EC\uADF8\uC778\uACFC \uD568\uAED8 \uB124\uC774\uD2F0\uBE0C CSS \uBCC0\uC218\uB97C \uC0AC\uC6A9\uD558\uB3C4\uB85D \uAD8C\uACE0\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uB2E4\uC2DC \uB9D0\uD574, vite\uB294 \uAE30\uBCF8\uC801\uC73C\uB85C <code>.scss</code>, <code>.sass</code>, <code>.less</code>, <code>.styl</code>, <code>.stylus</code>\uC640 CSS \uC804\uCC98\uB9AC\uAE30\uAC00 \uD544\uC694\uD55C \uD30C\uC77C\uC744 \uC9C0\uC6D0\uD558\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4. \uB2E4\uB9CC \uD544\uC694\uD55C \uACBD\uC6B0 \uC5B4\uB835\uC9C0 \uC54A\uAC8C \uC124\uCE58\uD574 \uBC14\uB85C \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="language-bash"><pre><code><span class="token comment"># .scss \uADF8\uB9AC\uACE0 .sass</span>
<span class="token function">npm</span> <span class="token function">add</span> -D sass

<span class="token comment"># .less</span>
<span class="token function">npm</span> <span class="token function">add</span> -D <span class="token function">less</span>

<span class="token comment"># .styl \uADF8\uB9AC\uACE0 .stylus</span>
<span class="token function">npm</span> <span class="token function">add</span> -D stylus
</code></pre></div><p>Vue \uC2F1\uAE00 \uD30C\uC77C \uCEF4\uD3EC\uB10C\uD2B8\uB97C \uC0AC\uC6A9\uD558\uB294 \uACBD\uC6B0, \uC124\uCE58 \uD6C4 \uBCC4 \uB2E4\uB978 \uC124\uC815 \uC5C6\uC774\uB3C4 <code>&lt;style lang=&quot;sass&quot;&gt;</code>\uC640 \uAC19\uC740 CSS \uC804\uCC98\uB9AC\uAE30\uB97C \uBC14\uB85C \uC0AC\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>Sass\uB098 Less\uC5D0\uC11C\uC758 <code>@import</code> \uBCC4\uCE6D \uB610\uD55C Vite\uC5D0\uC11C \uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4. <code>url()</code>\uB85C \uCC38\uC870\uB418\uB294 \uD30C\uC77C\uB4E4 \uC5ED\uC2DC \uC790\uB3D9\uC73C\uB85C \uC62C\uBC14\uB978 URL\uC744 \uAC16\uB3C4\uB85D \uC7AC\uC815\uC758\uB418\uACE0 \uB9D0\uC774\uC8E0.</p><p>\uB2E4\uB9CC Stylus\uC758 \uACBD\uC6B0 API \uCDA9\uB3CC\uB85C \uC778\uD574 Vite\uC758 <code>@import</code> \uBCC4\uCE6D\uACFC URL \uC7AC\uC815\uC758 \uAE30\uB2A5\uC774 \uC81C\uACF5\uB418\uC9C0 \uC54A\uC2B5\uB2C8\uB2E4.</p><p>\uBB3C\uB860 \uC804\uCC98\uB9AC\uB41C CSS \uC5ED\uC2DC <code>style.module.scss</code>\uC640 \uAC19\uC774 CSS \uBAA8\uB4C8\uCC98\uB7FC \uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4. \uB611\uAC19\uC774 \uD30C\uC77C \uD655\uC7A5\uC790\uC5D0 <code>.module</code>\uB9CC \uBD99\uC5EC\uC8FC\uC138\uC694.</p><h2 id="static-assets" tabindex="-1">\uC815\uC801 \uC5D0\uC14B <a class="header-anchor" href="#static-assets" aria-hidden="true">#</a></h2><p>\uC815\uC801 \uC5D0\uC14B\uC744 Import \uD558\uB294 \uACBD\uC6B0, \uC774\uC5D0 \uB300\uD55C Public URL\uC774 \uBC18\uD658\uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token keyword">import</span> imgUrl <span class="token keyword">from</span> <span class="token string">&#39;./img.png&#39;</span>
document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">&#39;hero-img&#39;</span><span class="token punctuation">)</span><span class="token punctuation">.</span>src <span class="token operator">=</span> imgUrl
</code></pre></div><p>URL \uCFFC\uB9AC\uB97C \uC774\uC6A9\uD574 \uC5D0\uC14B\uC744 \uAC00\uC838\uC62C \uB54C \uC5B4\uB5BB\uAC8C \uC774\uB97C \uAC00\uC838\uC62C \uAC83\uC778\uC9C0 \uBA85\uC2DC\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// URL\uB85C \uC5D0\uC14B \uAC00\uC838\uC624\uAE30</span>
<span class="token keyword">import</span> assetAsURL <span class="token keyword">from</span> <span class="token string">&#39;./asset.js?url&#39;</span>
</code></pre></div><div class="language-js"><pre><code><span class="token comment">// String \uD0C0\uC785\uC73C\uB85C \uC5D0\uC14B \uAC00\uC838\uC624\uAE30</span>
<span class="token keyword">import</span> assetAsString <span class="token keyword">from</span> <span class="token string">&#39;./shader.glsl?raw&#39;</span>
</code></pre></div><div class="language-js"><pre><code><span class="token comment">// \uC6F9 \uC6CC\uCEE4 \uAC00\uC838\uC624\uAE30</span>
<span class="token keyword">import</span> Worker <span class="token keyword">from</span> <span class="token string">&#39;./worker.js?worker&#39;</span>
</code></pre></div><div class="language-js"><pre><code><span class="token comment">// Base64 \uD3EC\uB9F7\uC758 \uBB38\uC790\uC5F4 \uD615\uD0DC\uB85C \uC6F9 \uC6CC\uCEE4 \uAC00\uC838\uC624\uAE30</span>
<span class="token keyword">import</span> InlineWorker <span class="token keyword">from</span> <span class="token string">&#39;./worker.js?worker&amp;inline&#39;</span>
</code></pre></div><p>\uC880 \uB354 \uC790\uC138\uD788 \uC54C\uACE0 \uC2F6\uB2E4\uBA74, <a href="./assets.html">\uC815\uC801 \uC5D0\uC14B \uD578\uB4E4\uB9C1\uD558\uAE30</a> \uC139\uC158\uC744 \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><h2 id="json" tabindex="-1">JSON <a class="header-anchor" href="#json" aria-hidden="true">#</a></h2><p>JSON \uD30C\uC77C\uC740 \uBC14\uB85C Import\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4. \uBB3C\uB860, \uAC00\uC838\uC62C \uD544\uB4DC\uB97C \uC9C0\uC815\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// \uAC1D\uCCB4 \uD615\uD0DC\uB85C \uAC00\uC838\uC624\uAE30</span>
<span class="token keyword">import</span> json <span class="token keyword">from</span> <span class="token string">&#39;./example.json&#39;</span>
<span class="token comment">// \uD544\uB4DC\uB97C \uC9C0\uC815\uD574 \uAC00\uC838\uC624\uAE30 (\uD2B8\uB9AC-\uC250\uC774\uD0B9 \uB429\uB2C8\uB2E4.)</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> field <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;./example.json&#39;</span>
</code></pre></div><h2 id="glob-import" tabindex="-1">Glob Import <a class="header-anchor" href="#glob-import" aria-hidden="true">#</a></h2><p>vite\uB294 <code>import.<wbr>meta.glob</code> \uD568\uC218\uB97C \uC774\uC6A9\uD574 \uC5EC\uB7EC \uBAA8\uB4C8\uC744 \uD55C \uBC88\uC5D0 \uAC00\uC838\uC62C \uC218 \uC788\uB3C4\uB85D \uC9C0\uC6D0\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC774 \uB54C, Glob \uD328\uD134*\uC744 \uC774\uC6A9\uD569\uB2C8\uB2E4. (* <a href="https://en.wikipedia.org/wiki/Glob_(programming)" target="_blank" rel="noopener noreferrer">Glob \uD328\uD134 Wikipedia</a>)</p><div class="language-js"><pre><code><span class="token keyword">const</span> modules <span class="token operator">=</span> <span class="token keyword">import</span><span class="token punctuation">.</span>meta<span class="token punctuation">.</span><span class="token function">glob</span><span class="token punctuation">(</span><span class="token string">&#39;./dir/*.js&#39;</span><span class="token punctuation">)</span>
</code></pre></div><p>\uC704 \uCF54\uB4DC\uB294 \uC544\uB798\uC640 \uAC19\uC774 \uBCC0\uD658\uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// Vite\uB97C \uD1B5\uD574 \uBCC0\uD658\uB41C \uCF54\uB4DC</span>
<span class="token keyword">const</span> modules <span class="token operator">=</span> <span class="token punctuation">{</span>
  <span class="token string-property property">&#39;./dir/foo.js&#39;</span><span class="token operator">:</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token keyword">import</span><span class="token punctuation">(</span><span class="token string">&#39;./dir/foo.js&#39;</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
  <span class="token string-property property">&#39;./dir/bar.js&#39;</span><span class="token operator">:</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token keyword">import</span><span class="token punctuation">(</span><span class="token string">&#39;./dir/bar.js&#39;</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\uC774\uB807\uAC8C \uAC00\uC838\uC628 <code>modules</code>\uB97C \uC21C\uD68C\uD558\uC5EC \uAC01 \uBAA8\uB4C8\uC5D0 \uC811\uADFC\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token keyword">for</span> <span class="token punctuation">(</span><span class="token keyword">const</span> path <span class="token keyword">in</span> modules<span class="token punctuation">)</span> <span class="token punctuation">{</span>
  modules<span class="token punctuation">[</span>path<span class="token punctuation">]</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">then</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">mod</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
    console<span class="token punctuation">.</span><span class="token function">log</span><span class="token punctuation">(</span>path<span class="token punctuation">,</span> mod<span class="token punctuation">)</span>
  <span class="token punctuation">}</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\uAE30\uBCF8\uC801\uC73C\uB85C <code>import.<wbr>meta.glob</code> \uD568\uC218\uB97C \uC774\uC6A9\uD558\uBA74, Dynamic Import\uB97C \uC774\uC6A9\uD574 Lazy\uD558\uAC8C \uD30C\uC77C\uC758 \uCCAD\uD06C\uB97C \uAC00\uC838\uC635\uB2C8\uB2E4. \uB9CC\uC57D \uB3D9\uC801\uC73C\uB85C(Dynamic) Import\uD558\uB294 \uAC83\uC774 \uC544\uB2C8\uB77C \uC9C1\uC811 \uBAA8\uB4C8\uC744 \uAC00\uC838\uC624\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>import.<wbr>meta.globEager</code> \uD568\uC218\uB97C \uC774\uC6A9\uD574\uC8FC\uC138\uC694.</p><div class="language-js"><pre><code><span class="token keyword">const</span> modules <span class="token operator">=</span> <span class="token keyword">import</span><span class="token punctuation">.</span>meta<span class="token punctuation">.</span><span class="token function">globEager</span><span class="token punctuation">(</span><span class="token string">&#39;./dir/*.js&#39;</span><span class="token punctuation">)</span>
</code></pre></div><p>\uC704 \uCF54\uB4DC\uB294 \uC544\uB798\uC640 \uAC19\uC774 \uBCC0\uD658\uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token comment">// Vite\uB97C \uD1B5\uD574 \uBCC0\uD658\uB41C \uCF54\uB4DC</span>
<span class="token keyword">import</span> <span class="token operator">*</span> <span class="token keyword">as</span> __glob__0_0 <span class="token keyword">from</span> <span class="token string">&#39;./dir/foo.js&#39;</span>
<span class="token keyword">import</span> <span class="token operator">*</span> <span class="token keyword">as</span> __glob__0_1 <span class="token keyword">from</span> <span class="token string">&#39;./dir/bar.js&#39;</span>
<span class="token keyword">const</span> modules <span class="token operator">=</span> <span class="token punctuation">{</span>
  <span class="token string-property property">&#39;./dir/foo.js&#39;</span><span class="token operator">:</span> __glob__0_0<span class="token punctuation">,</span>
  <span class="token string-property property">&#39;./dir/bar.js&#39;</span><span class="token operator">:</span> __glob__0_1
<span class="token punctuation">}</span>
</code></pre></div><p>\uCD94\uAC00\uC801\uC73C\uB85C, Glob \uD328\uD134\uACFC \uAD00\uB828\uD558\uC5EC \uB2E4\uC74C\uC758 \uC0AC\uD56D\uC744 \uC720\uC758\uD574\uC8FC\uC138\uC694.</p><ul><li>\uC774 \uAE30\uB2A5\uB4E4\uC740 Vite\uC5D0\uC11C \uC81C\uACF5\uD558\uB294 \uAE30\uB2A5\uC785\uB2C8\uB2E4. (ES \uD45C\uC900\uC774\uB098 \uC6F9 \uBE0C\uB77C\uC6B0\uC800\uC5D0\uC11C \uC81C\uACF5\uD558\uB294 \uAE30\uB2A5\uC774 \uC544\uB2C8\uC5D0\uC694.)</li><li>Glob \uD328\uD134 \uC0AC\uC6A9 \uC2DC, \uC0C1\uB300 \uACBD\uB85C(<code>./</code>) \uB610\uB294 \uC808\uB300 \uACBD\uB85C(<code>/</code>)\uB9CC\uC744 \uC774\uC6A9\uD574\uC57C \uD569\uB2C8\uB2E4.</li><li>Glob \uD328\uD134 \uB9E4\uCE6D\uC740 <code>fast-glob</code>\uC744 \uC774\uC6A9\uD569\uB2C8\uB2E4. \uC790\uC138\uD55C \uAC83\uC740 <a href="https://github.com/mrmlnc/fast-glob#pattern-syntax" target="_blank" rel="noopener noreferrer">\uC9C0\uC6D0\uD558\uB294 Glob \uD328\uD134 \uBAA9\uB85D</a>\uC744 \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</li><li>Glob\uC744 \uC774\uC6A9\uD55C <code>import</code>\uB294 \uBCC0\uC218\uB97C \uD5C8\uC6A9\uD558\uC9C0 \uC54A\uAE30 \uB54C\uBB38\uC5D0, \uBB38\uC790\uC5F4 \uD328\uD134\uC744 \uC9C1\uC811 \uC804\uB2EC\uD574\uC57C\uB9CC \uD569\uB2C8\uB2E4.</li><li>Glob \uD328\uD134\uC758 \uAC00\uC7A5 \uC678\uBD80\uC5D0 \uC704\uCE58\uD55C \uB530\uC634\uD45C\uB294 \uC778\uC6A9 \uBB38\uC790\uB97C \uC758\uBBF8\uD558\uB294 \uB530\uC634\uD45C(<code>&#39;</code>, <code>&quot;</code>, <code>\`</code>)\uC640 \uAC19\uC774 \uC0AC\uC6A9\uD560 \uC218 \uC5C6\uC2B5\uB2C8\uB2E4. \uAC00\uB839 <code>&#39;/Tom\\&#39;s files/**&#39;</code> \uB300\uC2E0 <code>&quot;/Tom&#39;s files/**&quot;</code> \uC640 \uAC19\uC774 \uC0AC\uC6A9\uD574\uC8FC\uC138\uC694.</li></ul><h2 id="web-assembly" tabindex="-1">WebAssembly <a class="header-anchor" href="#web-assembly" aria-hidden="true">#</a></h2><p>\uCEF4\uD30C\uC77C \uB41C <code>.wasm</code> \uD30C\uC77C \uC5ED\uC2DC Import\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4. Wasm \uD30C\uC77C\uC758 <code>export default</code>\uB85C Wasm \uC778\uC2A4\uD134\uC2A4\uC5D0\uC11C Export\uD55C \uAC1D\uCCB4\uB97C <code>Promise</code> \uD615\uD0DC\uB85C \uBC18\uD658\uD558\uB294 \uCD08\uAE30\uD654 \uD568\uC218\uAC00 \uB4E4\uC5B4\uAC00 \uC788\uC73C\uBA70, \uC774\uB97C \uD638\uCD9C\uD558\uB294 \uBC29\uC2DD\uC73C\uB85C \uC0AC\uC6A9\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token keyword">import</span> init <span class="token keyword">from</span> <span class="token string">&#39;./example.wasm&#39;</span>

<span class="token function">init</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">then</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token parameter">exports</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
  exports<span class="token punctuation">.</span><span class="token function">test</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\uCC38\uACE0\uB85C \uCD08\uAE30\uD654 \uD568\uC218\uB97C \uD638\uCD9C\uD560 \uB54C <code>imports</code> \uC635\uC158\uC744 \uC0AC\uC6A9\uD560 \uC218 \uC788\uB294\uB370, \uC774 \uAC12\uC740 <code>WebAssembly.instantiate</code> \uD568\uC218*\uC758 \uB450 \uBC88\uC9F8 \uC778\uC790 \uAC12\uC73C\uB85C \uC804\uB2EC\uB429\uB2C8\uB2E4. (* <a href="https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/WebAssembly/instantiate" target="_blank" rel="noopener noreferrer"><code>WebAssembly.instantiate</code> MDN doc</a>)</p><div class="language-js"><pre><code><span class="token function">init</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">imports</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token function-variable function">someFunc</span><span class="token operator">:</span> <span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
      <span class="token comment">/* ... */</span>
    <span class="token punctuation">}</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">then</span><span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token operator">=&gt;</span> <span class="token punctuation">{</span>
  <span class="token comment">/* ... */</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\uD504\uB85C\uB355\uC158 \uBE4C\uB4DC \uC2DC <code>assetsInlineLimit</code> \uC635\uC158*\uC758 \uAC12\uBCF4\uB2E4 \uC791\uC740 <code>.wasm</code> \uD30C\uC77C\uC740 Base64 \uBB38\uC790\uC5F4 \uD3EC\uB9F7\uC73C\uB85C \uBCC0\uD658\uB429\uB2C8\uB2E4. \uADF8\uB807\uC9C0 \uC54A\uC740 \uACBD\uC6B0, <code>dist</code> \uB514\uB809\uD130\uB9AC\uC5D0 \uD30C\uC77C\uC774 \uBCF5\uC0AC\uB418\uC5B4 \uC694\uCCAD(Fetch) \uC2DC \uBD88\uB7EC\uC624\uB294 \uBC29\uC2DD\uC73C\uB85C \uB3D9\uC791\uD558\uAC8C \uB429\uB2C8\uB2E4. (* <a href="/config/#build-assetsinlinelimit"><code>assetsInlineLimit</code> doc</a>)</p><h2 id="web-workers" tabindex="-1">Web Workers <a class="header-anchor" href="#web-workers" aria-hidden="true">#</a></h2><p>\uC6F9 \uC6CC\uCEE4\uC758 \uACBD\uC6B0 <code>?worker</code> \uB610\uB294 <code>?sharedworker</code> \uBB38\uC790\uC5F4\uC744 \uBD99\uC5EC Import \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uBAA8\uB4C8\uC758 <code>export default</code>\uB85C\uB294 \uC6CC\uCEE4\uC758 \uC0DD\uC131\uC790\uAC00 \uB4E4\uC5B4\uAC00\uAC8C \uB429\uB2C8\uB2E4.</p><div class="language-js"><pre><code><span class="token keyword">import</span> MyWorker <span class="token keyword">from</span> <span class="token string">&#39;./worker?worker&#39;</span>

<span class="token keyword">const</span> worker <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">MyWorker</span><span class="token punctuation">(</span><span class="token punctuation">)</span>
</code></pre></div><p>\uBB3C\uB860, <code>import</code> \uB300\uC2E0 <code>importScripts()</code> \uD568\uC218*\uB97C \uC774\uC6A9\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4. \uB2E4\uB9CC \uC774 \uACBD\uC6B0 \uAC1C\uBC1C \uD658\uACBD\uC5D0\uC11C\uB294 \uBE0C\uB77C\uC6B0\uC800\uC758 \uB124\uC774\uD2F0\uBE0C API\uC5D0\uB9CC \uC758\uC874\uD558\uC5EC \uD06C\uB86C\uC5D0\uC11C\uB9CC \uB3D9\uC791\uD55C\uB2E4\uB294 \uAC83\uC744 \uC720\uC758\uD574\uC8FC\uC138\uC694. \uBB3C\uB860 \uD504\uB85C\uB355\uC158 \uBE4C\uB4DC \uC2DC \uB2E4\uC591\uD55C \uBE0C\uB77C\uC6B0\uC800\uB97C \uC9C0\uC6D0\uD558\uB3C4\uB85D \uCEF4\uD30C\uC77C\uB429\uB2C8\uB2E4. (* <a href="https://developer.mozilla.org/en-US/docs/Web/API/WorkerGlobalScope/importScripts" target="_blank" rel="noopener noreferrer"><code>importScripts()</code> MDN doc</a>)</p><p>\uB9C8\uC9C0\uB9C9\uC73C\uB85C, \uAE30\uBCF8\uC801\uC73C\uB85C \uC6CC\uCEE4\uC758 \uACBD\uC6B0 \uD504\uB85C\uB355\uC158 \uBE4C\uB4DC \uBD84\uB9AC\uB41C \uCCAD\uD06C\uB85C \uCEF4\uD30C\uC77C\uB429\uB2C8\uB2E4. \uB9CC\uC57D \uBD84\uB9AC\uB41C \uCCAD\uD06C\uAC00 \uC544\uB2C8\uB77C Base64 \uD3EC\uB9F7\uC758 \uBB38\uC790\uC5F4\uB85C \uC774\uB97C \uC0AC\uC6A9\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>inline</code> \uCFFC\uB9AC\uB97C \uC774\uC6A9\uD574\uC8FC\uC138\uC694.</p><div class="language-js"><pre><code><span class="token keyword">import</span> MyWorker <span class="token keyword">from</span> <span class="token string">&#39;./worker?worker&amp;inline&#39;</span>
</code></pre></div><h2 id="build-optimizations" tabindex="-1">\uBE4C\uB4DC \uCD5C\uC801\uD654 <a class="header-anchor" href="#build-optimizations" aria-hidden="true">#</a></h2><blockquote><p>\uC544\uB798\uB294 \uCD94\uAC00\uC801\uC778 \uC124\uC815 \uC5C6\uC774 \uAE30\uBCF8\uC801\uC73C\uB85C \uBE4C\uB4DC \uD504\uB85C\uC138\uC2A4\uC5D0 \uC801\uC6A9\uB418\uB294 \uAE30\uB2A5\uB4E4\uC5D0 \uB300\uD55C \uBAA9\uB85D\uC785\uB2C8\uB2E4. \uBB3C\uB860, \uD544\uC694 \uC2DC \uAC01\uAC01\uC758 \uAE30\uB2A5\uB4E4\uC5D0 \uB300\uD55C \uBE44\uD65C\uC131\uD654\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p></blockquote><h3 id="css-code-splitting" tabindex="-1">CSS \uCF54\uB4DC \uBD84\uB9AC <a class="header-anchor" href="#css-code-splitting" aria-hidden="true">#</a></h3><p>vite\uB294 \uBE44\uB3D9\uAE30\uC801\uC73C\uB85C \uBD88\uB7EC\uC640\uC9C0\uB294 \uCCAD\uD06C \uB0B4\uC5D0 CSS \uCF54\uB4DC\uAC00 \uD3EC\uD568\uB41C \uACBD\uC6B0, \uC774\uB97C \uC790\uB3D9\uC73C\uB85C \uCD94\uCD9C\uD574 \uD30C\uC77C\uB85C \uBD84\uB9AC\uD569\uB2C8\uB2E4. \uC774\uD6C4 \uD574\uB2F9 \uCCAD\uD06C\uB97C \uBD88\uB7EC\uC62C \uB54C <code>&lt;link&gt;</code> \uD0DC\uADF8\uB97C \uC774\uC6A9\uD574 \uBD84\uB9AC\uB41C CSS \uCF54\uB4DC\uB97C \uBD88\uB7EC\uC624\uAC8C\uB054 \uD558\uBA70, CSS\uAC00 \uBAA8\uB450 \uACC4\uC0B0\uB41C \uD6C4\uC5D0 \uCCAD\uD06C\uB97C \uB80C\uB354\uD558\uB3C4\uB85D \uD569\uB2C8\uB2E4. \uAD73\uC774 \uC65C \uC774\uB807\uAC8C \uBCF5\uC7A1\uD55C \uACFC\uC815\uC744 \uAC70\uCE60\uAE4C\uC694? \uBC14\uB85C \uC774 \uACFC\uC815\uC744 \uD1B5\uD574 CSS\uAC00 \uB80C\uB354\uB9C1\uB420 \uB54C \uD654\uBA74\uC774 \uC7A0\uAE50 \uBC18\uC9DD\uC774\uB294 <a href="https://en.wikipedia.org/wiki/Flash_of_unstyled_content#:~:text=A%20flash%20of%20unstyled%20content,before%20all%20information%20is%20retrieved." target="_blank" rel="noopener noreferrer">FOUC \uD604\uC0C1</a>\uC744 \uD68C\uD53C\uD560 \uC218 \uC788\uAC8C \uB418\uAE30 \uB54C\uBB38\uC785\uB2C8\uB2E4.</p><p>\uBB3C\uB860, \uBAA8\uB4E0 CSS\uAC00 \uADF8\uB0E5 \uC77C\uBC18\uC801\uC778 \uAC01\uAC01\uC758 \uD30C\uC77C\uB85C \uC800\uC7A5\uB41C \uACBD\uC6B0\uB77C\uBA74 \uAD73\uC774 \uC774\uB7EC\uD55C \uAE30\uB2A5\uC744 \uC0AC\uC6A9\uD560 \uD544\uC694\uAC00 \uC5C6\uC2B5\uB2C8\uB2E4. \uC774\uB7EC\uD55C \uACBD\uC6B0 <a href="/config/#build-csscodesplit"><code>build.cssCodeSplit</code></a> \uC635\uC158\uC758 \uAC12\uC744 <code>false</code>\uB85C \uC124\uC815\uD574 \uBE44\uD65C\uC131\uD654\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p><h3 id="preload-directives-generation" tabindex="-1">Preload Directives Generation <a class="header-anchor" href="#preload-directives-generation" aria-hidden="true">#</a></h3><p>vite\uB294 \uBE4C\uB4DC \uC2DC Direct Import \uAD6C\uBB38\uC5D0 \uB300\uD574 <code>&lt;link ref=&quot;modulepreload&quot;&gt;</code> \uB514\uB809\uD2F0\uBE0C*\uB97C \uC774\uC6A9\uD574 \uBBF8\uB9AC \uBAA8\uB4C8\uC744 \uCE90\uC2F1\uD558\uB3C4\uB85D \uC790\uB3D9\uC73C\uB85C \uBCC0\uD658\uD569\uB2C8\uB2E4. \uB355\uBD84\uC5D0 \uD574\uB2F9 \uBAA8\uB4C8\uC744 \uD544\uC694\uB85C \uD558\uB294 \uACBD\uC6B0 \uC774\uB97C \uBC14\uB85C \uC0AC\uC6A9\uD560 \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4. (* <code>modulepreload</code>: \uB354 \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="https://developer.mozilla.org/en-US/docs/Web/HTML/Link_types/modulepreload" target="_blank" rel="noopener noreferrer">MDN doc</a> \uB610\uB294 <a href="https://developers.google.com/web/updates/2017/12/modulepreload" target="_blank" rel="noopener noreferrer">Google developers</a> \uBB38\uC11C\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.)</p><h3 id="async-chunk-loading-optimization" tabindex="-1">Async Chunk Loading Optimization <a class="header-anchor" href="#async-chunk-loading-optimization" aria-hidden="true">#</a></h3><p>\uBE4C\uB4DC \uC2DC, \uB54C\uB54C\uB85C Rollup\uC740 &quot;\uACF5\uD1B5(Common)&quot; \uCCAD\uD06C \uD30C\uC77C\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4. \uBCF4\uD1B5 \uB450 \uAC1C \uC774\uC0C1\uC758 \uBAA8\uB4C8\uC5D0\uC11C \uACF5\uC720\uB418\uB294 \uCCAD\uD06C\uAC00 \uC774\uB7EC\uD55C\uB370, \uC774\uB97C Dynamic Import\uB97C \uC774\uC6A9\uD574 \uBD88\uB7EC\uC624\uB294 \uACBD\uC6B0 \uB2E4\uC74C\uACFC \uAC19\uC740 \uC0C1\uD669\uC774 \uBC1C\uC0DD\uB429\uB2C8\uB2E4. (* \uBE0C\uB77C\uC6B0\uC800\uB294 <code>A</code>\uC640 <code>B</code> \uBAA8\uB4C8\uC744 \uD544\uC694\uB85C \uD558\uBA70(Dynamic Import), <code>A</code>\uC640 <code>B</code> \uBAA8\uB4C8\uC740 \uACF5\uD1B5\uC801\uC73C\uB85C \uBAA8\uB4C8 <code>C</code>\uB97C \uD544\uC694\uB85C \uD558\uB294 \uACBD\uC6B0(Direct Import)\uC785\uB2C8\uB2E4.)</p><p><img src="`+o+`" alt="graph"></p><p>\uCD5C\uC801\uD654\uB418\uC9C0 \uC54A\uC740 \uACBD\uC6B0, \uBA3C\uC800 \uBE44\uB3D9\uAE30\uC801\uC73C\uB85C <code>A</code> \uCCAD\uD06C\uAC00 \uBD88\uB7EC\uC640\uC9C0\uAC8C \uB418\uACE0, <code>A</code> \uCCAD\uD06C\uAC00 \uBAA8\uB450 \uD30C\uC2F1\uB41C \uD6C4\uC5D0\uC11C\uC57C <code>C</code> \uCCAD\uD06C\uAC00 \uD544\uC694\uD558\uB2E4\uB294 \uC0AC\uC2E4\uC744 \uC54C\uAC8C \uB418\uAE30\uC5D0 \uB2E4\uC74C\uACFC \uAC19\uC740 \uB124\uD2B8\uC6CC\uD06C \uC655\uBCF5\uC774 \uD544\uC694\uD569\uB2C8\uB2E4.</p><div class="language-"><pre><code>Entry ---&gt; A ---&gt; C
</code></pre></div><p>vite\uB294 Preload \uC2A4\uD15D\uC744 \uC774\uC6A9\uD574 <code>A</code>\uB97C \uAC00\uC838\uC62C \uB54C <code>C</code> \uCCAD\uD06C\uB97C <strong>\uBCD1\uB82C\uC801(Parallel)\uC73C\uB85C</strong> \uAC00\uC838\uC62C \uC218 \uC788\uB3C4\uB85D Dynamic Import \uAD6C\uBB38\uC744 \uC790\uB3D9\uC73C\uB85C \uC7AC\uC791\uC131\uD569\uB2C8\uB2E4.</p><div class="language-"><pre><code>Entry ---&gt; (A + C)
</code></pre></div><p>vite\uB294 \uBAA8\uB4E0 Direct Import \uAD6C\uBB38\uC5D0 \uB300\uD574 Preload \uD558\uB3C4\uB85D \uD568\uC73C\uB85C\uC368, \uC4F8 \uB370 \uC5C6\uC774 \uB0AD\uBE44\uB418\uB294 \uB124\uD2B8\uC6CC\uD06C \uC655\uBCF5\uC744 \uC904\uC774\uB3C4\uB85D \uAD6C\uC131\uD569\uB2C8\uB2E4.</p>`,124),r=[p];function c(l,i,d,u,k,m){return n(),s("div",null,r)}var f=e(t,[["render",c]]);export{h as __pageData,f as default};
