import{_ as e,c as a,o as n,a as s}from"./app.09e192c3.js";const g='{"title":"\uC815\uC801 \uC6F9 \uD398\uC774\uC9C0\uB85C \uBC30\uD3EC\uD558\uAE30","description":"","frontmatter":{"title":"\uC815\uC801 \uC6F9 \uD398\uC774\uC9C0\uB85C \uBC30\uD3EC\uD558\uAE30"},"headers":[{"level":2,"title":"\uC571 \uBE4C\uB4DC\uD558\uAE30","slug":"building-the-app"},{"level":3,"title":"\uB85C\uCEEC\uC5D0\uC11C \uC571 \uD14C\uC2A4\uD2B8\uD558\uAE30","slug":"testing-the-app-locally"},{"level":2,"title":"GitHub Pages","slug":"github-pages"},{"level":3,"title":"GitHub Pages \uADF8\uB9AC\uACE0 Travis CI","slug":"github-pages-and-travis-ci"},{"level":2,"title":"GitLab Pages \uADF8\uB9AC\uACE0 GitLab CI","slug":"github-pages-and-gitlab-ci"},{"level":2,"title":"Netlify","slug":"netlify"},{"level":2,"title":"Google Firebase","slug":"google-firebase"},{"level":2,"title":"Surge","slug":"surge"},{"level":2,"title":"Heroku","slug":"heroku"},{"level":2,"title":"Vercel","slug":"vercel"},{"level":3,"title":"Vercel CLI","slug":"vercel-cli"},{"level":3,"title":"Vercel for Git","slug":"vercel-for-git"},{"level":2,"title":"Azure Static Web Apps","slug":"azure-static-web-apps"}],"relativePath":"guide/static-deploy.md","lastUpdated":1644059343863}',t={},o=s(`<h1 id="deploying-a-static-site" tabindex="-1">\uC815\uC801 \uC6F9 \uD398\uC774\uC9C0\uB85C \uBC30\uD3EC\uD558\uAE30 <a class="header-anchor" href="#deploying-a-static-site" aria-hidden="true">#</a></h1><p>\uC774 \uACF3\uC758 \uB0B4\uC6A9\uC740 \uC544\uB798\uC640 \uAC19\uC774 \uC124\uC815\uD558\uC600\uB2E4\uACE0 \uAC00\uC815\uD55C \uC0C1\uD0DC\uC5D0\uC11C \uC9C4\uD589\uD569\uB2C8\uB2E4.</p><ul><li>\uBE4C\uB4DC \uACB0\uACFC\uBB3C\uC774 \uC800\uC7A5\uB418\uB294 \uB514\uB809\uD130\uB9AC\uB97C \uAE30\uBCF8 \uB514\uB809\uD130\uB9AC(<code>dist</code>)\uB85C \uC9C0\uC815\uD55C \uC0C1\uD0DC\uC785\uB2C8\uB2E4. \uBB3C\uB860 <a href="/config/#build-outdir"><code>build.outDir</code> \uC124\uC815 \uAC12\uC744 \uC774\uC6A9\uD574 \uBC14\uAFC0 \uC218\uB294 \uC788\uC73C\uB098</a>, \uC5EC\uAE30\uC11C\uB294 <code>dist</code>\uB97C \uBE4C\uB4DC \uB514\uB809\uD130\uB9AC\uB85C \uC9C0\uC815\uD588\uB2E4 \uAC00\uC815\uD558\uACE0 \uC9C4\uD589\uD569\uB2C8\uB2E4.</li><li>NPM \uB610\uB294 NPM \uC2A4\uD06C\uB9BD\uD2B8\uB97C \uC2E4\uD589\uD560 \uC218 \uC788\uB294 Yarn\uACFC \uAC19\uC740 \uD328\uD0A4\uC9C0 \uB9E4\uB2C8\uC800\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</li><li>vite\uB294 \uB85C\uCEEC PC\uC5D0 \uC874\uC7AC\uD558\uB294 \uD504\uB85C\uC81D\uD2B8\uC5D0 \uAC1C\uBC1C\uC6A9(Dev) \uB514\uD39C\uB358\uC2DC\uB85C \uC124\uCE58\uB41C \uC0C1\uD0DC\uC774\uBA70, \uC544\uB798\uC640 \uAC19\uC774 NPM \uC2A4\uD06C\uB9BD\uD2B8\uB97C \uC124\uC815\uD55C \uC0C1\uD0DC\uC785\uB2C8\uB2E4.</li></ul><div class="language-json"><pre><code><span class="token punctuation">{</span>
  <span class="token property">&quot;scripts&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token property">&quot;build&quot;</span><span class="token operator">:</span> <span class="token string">&quot;vite build&quot;</span><span class="token punctuation">,</span>
    <span class="token property">&quot;preview&quot;</span><span class="token operator">:</span> <span class="token string">&quot;vite preview&quot;</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\uD55C \uAC00\uC9C0 \uC720\uC758\uD560 \uC810\uC740, <code>vite preview</code> \uBA85\uB839\uC740 \uB85C\uCEEC\uC5D0\uC11C \uC5B4\uB5A4 \uD615\uD0DC\uB85C \uBE4C\uB4DC\uAC00 \uB418\uB294\uC9C0 \uBBF8\uB9AC \uD655\uC778\uD558\uAE30 \uC704\uD55C \uC6A9\uB3C4\uC77C \uBFD0\uC774\uBA70, \uC2E4\uC81C \uBC30\uD3EC\uC6A9 \uC11C\uBC84\uB97C \uC758\uBBF8\uD558\uC9C0\uB294 \uC54A\uC2B5\uB2C8\uB2E4.</p><div class="tip custom-block"><p class="custom-block-title">\uCC38\uACE0</p><p>\uC774 \uAC00\uC774\uB4DC\uB294 Vite \uAE30\uBC18\uC758 \uC0AC\uC774\uD2B8\uB97C \uC815\uC801(Static)\uC73C\uB85C \uBC30\uD3EC\uD558\uAE30 \uC704\uD55C \uBC29\uBC95\uC744 \uC124\uBA85\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uBB3C\uB860 Vite\uB294 \uC11C\uBC84 \uCE21 \uB80C\uB354\uB9C1(SSR, Server Side Rendering)\uC744 \uC9C0\uC6D0\uD558\uACE0 \uC788\uC73C\uB098, \uD604\uC7AC\uB294 \uC2E4\uD5D8\uC801\uC778 \uAE30\uB2A5\uC785\uB2C8\uB2E4. \uCC38\uACE0\uB85C &#39;\uC11C\uBC84 \uCE21 \uB80C\uB354\uB9C1&#39;\uC774\uB780, Node.js\uB97C \uC774\uC6A9\uD574 \uB3D9\uC77C\uD55C \uC6F9 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC744 HTML\uB85C \uC0AC\uC804\uC5D0 \uB80C\uB354\uB9C1\uD55C \uB4A4, \uC774\uB97C \uD074\uB77C\uC774\uC5B8\uD2B8\uC5D0\uAC8C \uC81C\uACF5\uD558\uB294 \uBC29\uC2DD\uC758 \uD504\uB7F0\uD2B8\uC5D4\uB4DC \uD504\uB808\uC784\uC6CC\uD06C\uB97C \uC758\uBBF8\uD569\uB2C8\uB2E4. \uC774\uC5D0 \uB300\uD574 \uB354 \uC54C\uACE0\uC790 \uD55C\uB2E4\uBA74 <a href="./ssr.html">SSR \uAC00\uC774\uB4DC</a>\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694. \uB9CC\uC57D \uAE30\uC874\uC5D0 \uC0AC\uC6A9\uD558\uACE0 \uC788\uB294 \uC11C\uBC84\uCE21 \uD504\uB808\uC784\uC6CC\uD06C(Ror, Laravel \uB4F1)\uAC00 \uC788\uB2E4\uBA74, <a href="./backend-integration.html">\uBC31\uC5D4\uB4DC \uD1B5\uD569 \uAC00\uC774\uB4DC</a>\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p></div><h2 id="building-the-app" tabindex="-1">\uC571 \uBE4C\uB4DC\uD558\uAE30 <a class="header-anchor" href="#building-the-app" aria-hidden="true">#</a></h2><p>\uC544\uB798\uC640 \uAC19\uC774 <code>npm run build</code> \uBA85\uB839\uC744 \uD1B5\uD574 \uC571\uC744 \uBE4C\uB4DC\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><div class="language-bash"><pre><code>$ <span class="token function">npm</span> run build
</code></pre></div><p>\uAE30\uBCF8\uC801\uC73C\uB85C <code>dist</code> \uB514\uB809\uD130\uB9AC\uC5D0 \uBE4C\uB4DC \uACB0\uACFC\uBB3C\uC774 \uC800\uC7A5\uB418\uBA70, \uBC30\uD3EC \uC2DC <code>dist</code> \uB514\uB809\uD130\uB9AC\uB97C \uC6D0\uD558\uB294 \uD50C\uB7AB\uD3FC\uC5D0 \uB9DE\uCDB0 \uADF8\uB300\uB85C \uBC30\uD3EC\uD558\uBA74 \uB429\uB2C8\uB2E4.</p><h3 id="testing-the-app-locally" tabindex="-1">\uB85C\uCEEC\uC5D0\uC11C \uC571 \uD14C\uC2A4\uD2B8\uD558\uAE30 <a class="header-anchor" href="#testing-the-app-locally" aria-hidden="true">#</a></h3><p>\uD55C \uBC88 \uBE4C\uB4DC\uB41C \uC571\uC740 <code>npm run preview</code> \uBA85\uB839\uC73C\uB85C \uB85C\uCEEC\uC5D0\uC11C \uD14C\uC2A4\uD2B8\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p><div class="language-bash"><pre><code>$ <span class="token function">npm</span> run build
$ <span class="token function">npm</span> run preview
</code></pre></div><p><code>vite preview</code> \uBA85\uB839\uC744 \uC2E4\uD589\uD558\uAC8C \uB418\uBA74 \uC815\uC801 \uC6F9 \uC11C\uBC84\uAC00 \uC2E4\uD589\uB418\uBA70, \uC774 \uC11C\uBC84\uB294 <code>dist</code> \uB0B4\uC5D0 \uC874\uC7AC\uD558\uB294 \uD30C\uC77C\uC744 <a href="http://localhost:5000" target="_blank" rel="noopener noreferrer">http://localhost:5000</a> \uACBD\uB85C\uB97C \uD1B5\uD574 \uBC30\uD3EC\uD569\uB2C8\uB2E4. \uBE0C\uB77C\uC6B0\uC800\uB97C \uD1B5\uD574 \uC0AC\uC774\uD2B8\uC5D0 \uC811\uC18D\uD558\uC5EC \uC2E4\uC81C \uBC30\uD3EC \uC2DC \uC5B4\uB5BB\uAC8C \uBCF4\uC5EC\uC9C8 \uAC83\uC778\uC9C0 \uC27D\uAC8C \uD30C\uC545\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><p>\uB9CC\uC57D \uD2B9\uC815 \uD3EC\uD2B8\uB97C \uC9C0\uC815\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74 <code>--port</code> \uC635\uC158\uC744 \uC774\uC6A9\uD574\uC8FC\uC138\uC694.</p><div class="language-json"><pre><code><span class="token punctuation">{</span>
  <span class="token property">&quot;scripts&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token property">&quot;preview&quot;</span><span class="token operator">:</span> <span class="token string">&quot;vite preview --port 8080&quot;</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\uC774\uB807\uAC8C \uC124\uC815\uD55C \uACBD\uC6B0 <a href="http://localhost:8080" target="_blank" rel="noopener noreferrer">http://localhost:8080</a> \uC744 \uAE30\uC900\uC73C\uB85C <code>preview</code> \uBA85\uB839\uC774 \uC2E4\uD589\uB429\uB2C8\uB2E4.</p><div class="tip custom-block"><p class="custom-block-title">\uCC38\uACE0</p><p><code>serve</code>\uC5D0\uC11C <code>preview</code>\uB85C \uC2A4\uD06C\uB9BD\uD2B8\uC758 \uC774\uB984\uC744 \uBC14\uAFB8\uB294 \uACBD\uC6B0, \uC77C\uBD80 \uD328\uD0A4\uC9C0 \uB9E4\uB2C8\uC800\uC5D0\uC11C <a href="https://docs.npmjs.com/cli/v7/using-npm/scripts#pre--post-scripts" target="_blank" rel="noopener noreferrer">Pre &amp; Post \uC2A4\uD06C\uB9BD\uD2B8</a>\uB97C \uCC98\uB9AC\uD558\uB294 \uBC29\uC2DD\uC73C\uB85C \uC778\uD574 \uBB38\uC81C\uAC00 \uBC1C\uC0DD\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p></div><h2 id="github-pages" tabindex="-1">GitHub Pages <a class="header-anchor" href="#github-pages" aria-hidden="true">#</a></h2><ol><li><p><code>vite.config.js</code> \uD30C\uC77C \uB0B4 <code>base</code> \uC124\uC815 \uAC12\uC744 \uC801\uC808\uD558\uAC8C \uC124\uC815\uD569\uB2C8\uB2E4.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME&gt;.github.io/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 \uC0DD\uB7B5\uD558\uAC70\uB098 \uAE30\uBCF8 \uAC12\uC778 <code>&#39;/&#39;</code> \uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME&gt;.github.io/&lt;REPO&gt;/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 <code>&#39;/&lt;REPO&gt;/&#39;</code>\uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p></li><li><p>\uD504\uB85C\uC81D\uD2B8\uC758 \uB8E8\uD2B8\uC5D0 \uC544\uB798\uC640 \uAC19\uC740 \uB0B4\uC6A9\uC774 \uB4E4\uC5B4\uAC04 <code>deploy.sh</code> \uD30C\uC77C\uC744 \uC0DD\uC131 \uBC0F \uC2E4\uD589\uD574\uC8FC\uC138\uC694(\uD558\uC774\uB77C\uC774\uD2B8 \uB41C \uB77C\uC778\uC740 \uD544\uC694\uC5D0 \uB530\uB77C \uC8FC\uC11D \uCC98\uB9AC\uB97C \uD480\uC5B4\uC8FC\uC138\uC694).</p><div class="language-bash"><div class="highlight-lines"><br><br><br><br><br><br><br><br><br><br><br><br><div class="highlighted">\xA0</div><br><br><br><br><br><br><div class="highlighted">\xA0</div><br><br><div class="highlighted">\xA0</div><br><br><br></div><pre><code><span class="token shebang important">#!/usr/bin/env sh</span>

<span class="token comment"># \uC5D0\uB7EC\uAC00 \uBC1C\uC0DD\uB420 \uACBD\uC6B0 \uC2A4\uD06C\uB9BD\uD2B8 \uC2E4\uD589\uC744 \uC911\uC9C0</span>
<span class="token builtin class-name">set</span> -e

<span class="token comment"># \uC571 \uBE4C\uB4DC</span>
<span class="token function">npm</span> run build

<span class="token comment"># \uBE4C\uB4DC\uB41C \uD30C\uC77C\uC774 \uC874\uC7AC\uD558\uB294 dist \uB514\uB809\uD130\uB9AC\uB85C \uC774\uB3D9</span>
<span class="token builtin class-name">cd</span> dist

<span class="token comment"># CNAME \uD30C\uC77C\uC744 \uC774\uC6A9\uD574 \uCEE4\uC2A4\uD140 \uB3C4\uBA54\uC778\uC744 \uC9C0\uC815\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</span>
<span class="token comment"># echo &#39;www.example.com&#39; &gt; CNAME</span>

<span class="token function">git</span> init
<span class="token function">git</span> <span class="token function">add</span> -A
<span class="token function">git</span> commit -m <span class="token string">&#39;deploy&#39;</span>

<span class="token comment"># https://&lt;USERNAME&gt;.github.io \uC5D0 \uBC30\uD3EC</span>
<span class="token comment"># git push -f git@github.com:&lt;USERNAME&gt;/&lt;USERNAME&gt;.github.io.git main</span>

<span class="token comment"># https://&lt;USERNAME&gt;.github.io/&lt;REPO&gt; \uC5D0 \uBC30\uD3EC</span>
<span class="token comment"># git push -f git@github.com:&lt;USERNAME&gt;/&lt;REPO&gt;.git main:gh-pages</span>

<span class="token builtin class-name">cd</span> -
</code></pre></div></li></ol><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\uBB3C\uB860 CI \uD234\uC744 \uC774\uC6A9\uD574 \uC704 \uC2A4\uD06C\uB9BD\uD2B8 \uAE30\uBC18\uC73C\uB85C \uBC30\uD3EC\uAC00 \uC790\uB3D9\uC73C\uB85C \uC774\uB8E8\uC5B4\uC9C0\uAC8C\uB054 \uC124\uC815\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p></div><h3 id="github-pages-and-travis-ci" tabindex="-1">GitHub Pages \uADF8\uB9AC\uACE0 Travis CI <a class="header-anchor" href="#github-pages-and-travis-ci" aria-hidden="true">#</a></h3><ol><li><p><code>vite.config.js</code> \uD30C\uC77C \uB0B4 <code>base</code> \uC124\uC815 \uAC12\uC744 \uC801\uC808\uD558\uAC8C \uC9C0\uC815\uD569\uB2C8\uB2E4.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME or GROUP&gt;.github.io/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 \uC0DD\uB7B5\uD558\uAC70\uB098 \uAE30\uBCF8 \uAC12\uC778 <code>&#39;/&#39;</code>\uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME or GROUP&gt;.github.io/&lt;REPO&gt;/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 <code>&#39;/&lt;REPO&gt;/&#39;</code>\uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p></li><li><p>\uD504\uB85C\uC81D\uD2B8\uC758 \uB8E8\uD2B8\uC5D0 <code>.travis.yml</code> \uD30C\uC77C\uC744 \uC0DD\uC131\uD574\uC8FC\uC138\uC694(\uD30C\uC77C \uB0B4\uC6A9\uC740 \uC544\uB798 \uCF54\uB4DC\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694).</p></li><li><p><code>npm install</code> \uBA85\uB839\uC744 \uC2E4\uD589\uD55C \uB4A4, <code>package-lock.json</code> \uD30C\uC77C\uC744 \uCEE4\uBC0B\uD574\uC8FC\uC138\uC694.</p></li><li><p><a href="https://docs.travis-ci.com/user/deployment/pages/" target="_blank" rel="noopener noreferrer">Travis CI \uBB38\uC11C</a>\uB97C \uCC38\uACE0\uD574\uC11C GitHub Pages \uBC30\uD3EC\uB97C \uC704\uD55C \uC124\uC815\uC744 \uC9C4\uD589\uD574\uC8FC\uC138\uC694. \uC608\uB97C \uB4E4\uC5B4 \uC544\uB798\uC640 \uAC19\uC774 \uC124\uC815\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p><div class="language-yaml"><pre><code><span class="token key atrule">language</span><span class="token punctuation">:</span> node_js
<span class="token key atrule">node_js</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> lts/*
<span class="token key atrule">install</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> npm ci
<span class="token key atrule">script</span><span class="token punctuation">:</span>
  <span class="token punctuation">-</span> npm run build
<span class="token key atrule">deploy</span><span class="token punctuation">:</span>
  <span class="token key atrule">provider</span><span class="token punctuation">:</span> pages
  <span class="token key atrule">skip_cleanup</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
  <span class="token key atrule">local_dir</span><span class="token punctuation">:</span> dist
  <span class="token comment"># Travis\uAC00 \uB9AC\uD3EC\uC9C0\uD130\uB9AC\uC5D0 Push \uD560 \uC218 \uC788\uB3C4\uB85D GitHub\uC5D0\uC11C $GITHUB_TOKEN\uC744 \uC0DD\uC131\uD569\uB2C8\uB2E4.</span>
  <span class="token comment"># \uC0DD\uC131\uD55C $GITHUB_TOKEN\uC758 \uAC12\uC740 Travis \uC124\uC815 \uD398\uC774\uC9C0\uC5D0\uC11C \uD658\uACBD \uBCC0\uC218\uB85C \uC9C0\uC815\uD574 \uC0AC\uC6A9\uD560 \uC218 \uC788\uC5B4\uC694.</span>
  <span class="token key atrule">github_token</span><span class="token punctuation">:</span> $GITHUB_TOKEN
  <span class="token key atrule">keep_history</span><span class="token punctuation">:</span> <span class="token boolean important">true</span>
  <span class="token key atrule">on</span><span class="token punctuation">:</span>
    <span class="token key atrule">branch</span><span class="token punctuation">:</span> main
</code></pre></div></li></ol><p><code>$GITHUB_TOKEN</code>\uACFC \uAD00\uB828\uD55C \uB0B4\uC6A9\uC740 \uC544\uB798 \uBB38\uC11C\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><ul><li><a href="https://docs.github.com/en/github/authenticating-to-github/keeping-your-account-and-data-secure/creating-a-personal-access-token" target="_blank" rel="noopener noreferrer">\uAC1C\uC778 \uC561\uC138\uC2A4 \uD1A0\uD070 \uC0DD\uC131\uD558\uAE30 (GitHub)</a></li><li><a href="https://docs.travis-ci.com/user/deployment/pages/#setting-the-github-token" target="_blank" rel="noopener noreferrer">GitHub Token \uC124\uC815\uD558\uAE30 (Travis)</a></li></ul><h2 id="github-pages-and-gitlab-ci" tabindex="-1">GitLab Pages \uADF8\uB9AC\uACE0 GitLab CI <a class="header-anchor" href="#github-pages-and-gitlab-ci" aria-hidden="true">#</a></h2><ol><li><p><code>vite.config.js</code> \uD30C\uC77C \uB0B4 <code>base</code> \uC124\uC815 \uAC12\uC744 \uC801\uC808\uD558\uAC8C \uC9C0\uC815\uD569\uB2C8\uB2E4.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME or GROUP&gt;.gitlab.io/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 \uC0DD\uB7B5\uD558\uAC70\uB098 \uAE30\uBCF8 \uAC12\uC778 <code>&#39;/&#39;</code>\uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p><p>\uB9CC\uC57D <code>https://&lt;USERNAME or GROUP&gt;.gitlab.io/&lt;REPO&gt;/</code>\uC640 \uAC19\uC740 \uD615\uD0DC\uB85C \uBC30\uD3EC\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74, <code>base</code> \uC124\uC815 \uAC12\uC744 <code>&#39;/&lt;REPO&gt;/&#39;</code>\uB85C \uC9C0\uC815\uD574\uC8FC\uC138\uC694.</p></li><li><p>\uC544\uB798\uC640 \uAC19\uC740 \uB0B4\uC6A9\uC73C\uB85C \uD504\uB85C\uC81D\uD2B8\uC758 \uB8E8\uD2B8\uC5D0 <code>.gitlab-ci.yml</code> \uD30C\uC77C\uC744 \uC0DD\uC131\uD574\uC8FC\uC138\uC694. \uC774\uC640 \uAC19\uC774 \uC124\uC815\uD558\uAC8C \uB418\uBA74, \uCF58\uD150\uCE20\uAC00 \uBCC0\uACBD\uB420 \uB54C\uB9C8\uB2E4 \uC0AC\uC774\uD2B8\uAC00 \uBE4C\uB4DC \uBC0F \uBC30\uD3EC\uB429\uB2C8\uB2E4.</p><div class="language-yaml"><pre><code><span class="token key atrule">image</span><span class="token punctuation">:</span> node<span class="token punctuation">:</span>16.5.0
<span class="token key atrule">pages</span><span class="token punctuation">:</span>
  <span class="token key atrule">stage</span><span class="token punctuation">:</span> deploy
  <span class="token key atrule">cache</span><span class="token punctuation">:</span>
    <span class="token key atrule">key</span><span class="token punctuation">:</span>
      <span class="token key atrule">files</span><span class="token punctuation">:</span>
        <span class="token punctuation">-</span> package<span class="token punctuation">-</span>lock.json
      <span class="token key atrule">prefix</span><span class="token punctuation">:</span> npm
    <span class="token key atrule">paths</span><span class="token punctuation">:</span>
      <span class="token punctuation">-</span> node_modules/
  <span class="token key atrule">script</span><span class="token punctuation">:</span>
    <span class="token punctuation">-</span> npm install
    <span class="token punctuation">-</span> npm run build
    <span class="token punctuation">-</span> cp <span class="token punctuation">-</span>a dist/. public/
  <span class="token key atrule">artifacts</span><span class="token punctuation">:</span>
    <span class="token key atrule">paths</span><span class="token punctuation">:</span>
      <span class="token punctuation">-</span> public
  <span class="token key atrule">rules</span><span class="token punctuation">:</span>
    <span class="token punctuation">-</span> <span class="token key atrule">if</span><span class="token punctuation">:</span> $CI_COMMIT_BRANCH == $CI_DEFAULT_BRANCH
</code></pre></div></li></ol><h2 id="netlify" tabindex="-1">Netlify <a class="header-anchor" href="#netlify" aria-hidden="true">#</a></h2><ol><li><p><a href="https://netlify.com" target="_blank" rel="noopener noreferrer">Netlify</a>\uC5D0\uC11C \uC544\uB798\uC640 \uAC19\uC740 \uC124\uC815\uC73C\uB85C GitHub \uD504\uB85C\uC81D\uD2B8\uB97C \uC0DD\uC131\uD574\uC8FC\uC138\uC694.</p><ul><li><strong>Build Command:</strong> <code>vite build</code> \uB610\uB294 <code>npm run build</code></li><li><strong>Publish directory:</strong> <code>dist</code></li></ul></li><li><p>Deploy \uBC84\uD2BC\uC744 \uB20C\uB7EC\uC8FC\uC138\uC694.</p></li></ol><h2 id="google-firebase" tabindex="-1">Google Firebase <a class="header-anchor" href="#google-firebase" aria-hidden="true">#</a></h2><ol><li><p><a href="https://www.npmjs.com/package/firebase-tools" target="_blank" rel="noopener noreferrer">firebase-tools</a>\uAC00 \uC124\uCE58\uB418\uC5B4 \uC788\uB294\uC9C0 \uD655\uC778\uD574\uC8FC\uC138\uC694.</p></li><li><p>\uC544\uB798\uC640 \uAC19\uC740 \uB0B4\uC6A9\uC73C\uB85C \uD504\uB85C\uC81D\uD2B8 \uB8E8\uD2B8\uC5D0 <code>firebase.json</code> \uBC0F <code>.firebaserc</code> \uD30C\uC77C\uC744 \uC0DD\uC131\uD574\uC8FC\uC138\uC694.</p><p><code>firebase.json</code>:</p><div class="language-json"><pre><code><span class="token punctuation">{</span>
  <span class="token property">&quot;hosting&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token property">&quot;public&quot;</span><span class="token operator">:</span> <span class="token string">&quot;dist&quot;</span><span class="token punctuation">,</span>
    <span class="token property">&quot;ignore&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
    <span class="token property">&quot;rewrites&quot;</span><span class="token operator">:</span> <span class="token punctuation">[</span>
      <span class="token punctuation">{</span>
        <span class="token property">&quot;source&quot;</span><span class="token operator">:</span> <span class="token string">&quot;**&quot;</span><span class="token punctuation">,</span>
        <span class="token property">&quot;destination&quot;</span><span class="token operator">:</span> <span class="token string">&quot;/index.html&quot;</span>
      <span class="token punctuation">}</span>
    <span class="token punctuation">]</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div><p><code>.firebaserc</code>:</p><div class="language-js"><pre><code><span class="token punctuation">{</span>
  <span class="token string-property property">&quot;projects&quot;</span><span class="token operator">:</span> <span class="token punctuation">{</span>
    <span class="token string-property property">&quot;default&quot;</span><span class="token operator">:</span> <span class="token string">&quot;&lt;YOUR_FIREBASE_ID&gt;&quot;</span>
  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>
</code></pre></div></li><li><p><code>npm run build</code> \uBA85\uB839\uC744 \uBA3C\uC800 \uC2E4\uD589\uD558\uACE0, \uADF8 \uB2E4\uC74C <code>firebase deploy</code> \uBA85\uB839\uC744 \uD1B5\uD574 \uBC30\uD3EC\uAC00 \uAC00\uB2A5\uD569\uB2C8\uB2E4.</p></li></ol><h2 id="surge" tabindex="-1">Surge <a class="header-anchor" href="#surge" aria-hidden="true">#</a></h2><ol><li><p><a href="https://www.npmjs.com/package/surge" target="_blank" rel="noopener noreferrer">surge</a>\uAC00 \uC124\uCE58\uB418\uC9C0 \uC54A\uC740 \uACBD\uC6B0, \uBA3C\uC800 \uC124\uCE58\uD574\uC8FC\uC138\uC694.</p></li><li><p><code>npm run build</code> \uBA85\uB839\uC744 \uC2E4\uD589\uD574\uC8FC\uC138\uC694.</p></li><li><p><code>surge dist</code> \uBA85\uB839\uC744 \uD1B5\uD574 Surge\uB85C \uBC30\uD3EC\uD574\uC8FC\uC138\uC694.</p></li></ol><p>\uBB3C\uB860, <code>surge dist yourdomain.com</code>\uACFC \uAC19\uC740 \uBA85\uB839\uC744 \uC774\uC6A9\uD574 <a href="http://surge.sh/help/adding-a-custom-domain" target="_blank" rel="noopener noreferrer">\uCEE4\uC2A4\uD140 \uB3C4\uBA54\uC778</a>\uC73C\uB85C \uBC30\uD3EC\uD560 \uC218\uB3C4 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="heroku" tabindex="-1">Heroku <a class="header-anchor" href="#heroku" aria-hidden="true">#</a></h2><ol><li><p><a href="https://devcenter.heroku.com/articles/heroku-cli" target="_blank" rel="noopener noreferrer">Heroku CLI</a>\uB97C \uC124\uCE58\uD574\uC8FC\uC138\uC694.</p></li><li><p><a href="https://signup.heroku.com" target="_blank" rel="noopener noreferrer">Heroku \uAC00\uC785 \uD398\uC774\uC9C0</a>\uC5D0\uC11C \uACC4\uC815\uC744 \uB9CC\uB4E4\uC5B4\uC8FC\uC138\uC694.</p></li><li><p><code>heroku login</code> \uBA85\uB839\uC73C\uB85C Heroku\uC5D0 \uB85C\uADF8\uC778\uD574\uC8FC\uC138\uC694.</p><div class="language-bash"><pre><code>$ heroku login
</code></pre></div></li><li><p>\uC544\uB798\uC640 \uAC19\uC740 \uB0B4\uC6A9\uC73C\uB85C <code>static.json</code> \uD30C\uC77C\uC744 \uD504\uB85C\uC81D\uD2B8 \uB8E8\uD2B8\uC5D0 \uC0DD\uC131\uD574\uC8FC\uC138\uC694.</p><p><code>static.json</code>:</p><div class="language-json"><pre><code><span class="token punctuation">{</span>
  <span class="token property">&quot;root&quot;</span><span class="token operator">:</span> <span class="token string">&quot;./dist&quot;</span>
<span class="token punctuation">}</span>
</code></pre></div><p>\uC774\uB7EC\uD55C \uBC29\uC2DD\uC73C\uB85C \uBC30\uD3EC\uB420 \uC0AC\uC774\uD2B8\uC5D0 \uB300\uD55C \uC124\uC815\uC774 \uAC00\uB2A5\uD569\uB2C8\uB2E4. \uB354 \uB9CE\uC740 \uC815\uBCF4\uAC00 \uD544\uC694\uD558\uB2E4\uBA74 <a href="https://github.com/heroku/heroku-buildpack-static" target="_blank" rel="noopener noreferrer">heroku-buildpack-static</a> \uB9AC\uD3EC\uC9C0\uD130\uB9AC\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p></li><li><p>Heroku Git remote\uB97C \uC124\uC815\uD574\uC8FC\uC138\uC694.</p><div class="language-bash"><pre><code><span class="token comment"># \uBC84\uC804 \uAC31\uC2E0</span>
$ <span class="token function">git</span> init
$ <span class="token function">git</span> <span class="token function">add</span> <span class="token builtin class-name">.</span>
$ <span class="token function">git</span> commit -m <span class="token string">&quot;My site ready for deployment.&quot;</span>

<span class="token comment"># \uC0C8\uB85C\uC6B4 \uC560\uD50C\uB9AC\uCF00\uC774\uC158 \uC0DD\uC131</span>
$ heroku apps:create example

<span class="token comment"># \uC815\uC801 \uC0AC\uC774\uD2B8 \uBC30\uD3EC\uB97C \uC704\uD574 Buildpack \uC815\uC758</span>
$ heroku buildpacks:set https://github.com/heroku/heroku-buildpack-static.git
</code></pre></div></li><li><p>\uB9C8\uC9C0\uB9C9\uC73C\uB85C, \uC544\uB798 \uBA85\uB839\uC744 \uD1B5\uD574 \uC0AC\uC774\uD2B8\uB97C \uBC30\uD3EC\uD558\uAC8C \uB429\uB2C8\uB2E4.</p><div class="language-bash"><pre><code><span class="token comment"># \uC0AC\uC774\uD2B8 \uBC30\uD3EC</span>
$ <span class="token function">git</span> push heroku main

<span class="token comment"># Heroku CI \uB300\uC2DC\uBCF4\uB4DC \uC5F4\uAE30</span>
$ heroku <span class="token function">open</span>
</code></pre></div></li></ol><h2 id="vercel" tabindex="-1">Vercel <a class="header-anchor" href="#vercel" aria-hidden="true">#</a></h2><h3 id="vercel-cli" tabindex="-1">Vercel CLI <a class="header-anchor" href="#vercel-cli" aria-hidden="true">#</a></h3><ol><li><a href="https://vercel.com/cli" target="_blank" rel="noopener noreferrer">Vercel CLI</a>\uB97C \uC124\uCE58\uD558\uACE0 <code>vercel</code>\uC744 \uC2E4\uD589\uD558\uC5EC \uBC30\uD3EC\uD569\uB2C8\uB2E4.</li><li>Vercel\uC740 Vite\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC74C\uC744 \uAC10\uC9C0\uD558\uAC8C \uB418\uBA70, \uBC30\uD3EC\uC640 \uAD00\uB828\uB41C \uC62C\uBC14\uB978 \uC124\uC815\uC744 \uD65C\uC131\uD654\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uBC30\uD3EC\uB418\uC5C8\uC2B5\uB2C8\uB2E4! (\uC608\uC2DC: <a href="https://vite-vue-template.vercel.app/" target="_blank" rel="noopener noreferrer">vite-vue-template.vercel.app</a>)</li></ol><div class="language-bash"><pre><code>$ <span class="token function">npm</span> i -g vercel
$ vercel init vite
Vercel CLI
<span class="token operator">&gt;</span> Success<span class="token operator">!</span> Initialized <span class="token string">&quot;vite&quot;</span> example <span class="token keyword">in</span> ~/your-folder.
- To deploy, <span class="token variable"><span class="token variable">\`</span><span class="token builtin class-name">cd</span> vite<span class="token variable">\`</span></span> and run <span class="token variable"><span class="token variable">\`</span>vercel<span class="token variable">\`</span></span><span class="token builtin class-name">.</span>
</code></pre></div><h3 id="vercel-for-git" tabindex="-1">Vercel for Git <a class="header-anchor" href="#vercel-for-git" aria-hidden="true">#</a></h3><ol><li>\uC0AC\uC6A9\uD558\uACE0 \uC788\uB294 Git \uB9AC\uD3EC\uC9C0\uD130\uB9AC(GitHub, GitLab, BitBucket)\uC73C\uB85C \uC18C\uC2A4 \uCF54\uB4DC\uB97C Push \uD569\uB2C8\uB2E4.</li><li>Vercel\uB85C <a href="https://vercel.com/new" target="_blank" rel="noopener noreferrer">Vite \uD504\uB85C\uC81D\uD2B8\uB97C \uAC00\uC838\uC635\uB2C8\uB2E4</a>.</li><li>Vercel\uC740 Vite\uB97C \uC0AC\uC6A9\uD558\uACE0 \uC788\uC74C\uC744 \uAC10\uC9C0\uD558\uAC8C \uB418\uBA70, \uBC30\uD3EC\uC640 \uAD00\uB828\uB41C \uC62C\uBC14\uB978 \uC124\uC815\uC744 \uD65C\uC131\uD654\uD569\uB2C8\uB2E4.</li><li>\uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC774 \uBC30\uD3EC\uB418\uC5C8\uC2B5\uB2C8\uB2E4! (\uC608\uC2DC: <a href="https://vite-vue-template.vercel.app/" target="_blank" rel="noopener noreferrer">vite-vue-template.vercel.app</a>)</li></ol><p>Vercel\uB85C \uD504\uB85C\uC81D\uD2B8\uB97C \uBD88\uB7EC\uC624\uACE0 \uBC30\uD3EC\uAE4C\uC9C0 \uC644\uB8CC\uD588\uB2E4\uBA74, \uC774\uD6C4 \uBE0C\uB79C\uCE58\uC5D0 \uB300\uD55C \uBAA8\uB4E0 Push \uB3D9\uC791\uC740 \uC560\uD50C\uB9AC\uCF00\uC774\uC158\uC5D0 \uB300\uD55C <a href="https://vercel.com/docs/concepts/deployments/environments#preview" target="_blank" rel="noopener noreferrer">\uBBF8\uB9AC\uBCF4\uAE30\uC6A9 \uBC30\uD3EC</a>\uB97C \uC0DD\uC131\uD558\uAC8C \uB429\uB2C8\uB2E4. \uADF8\uB9AC\uACE0 \uD504\uB85C\uB355\uC158\uC6A9 \uBE0C\uB79C\uCE58(\uC77C\uBC18\uC801\uC73C\uB85C &quot;main&quot;)\uC5D0 \uB300\uD55C \uBAA8\uB4E0 \uBCC0\uACBD \uC0AC\uD56D\uC740 <a href="https://vercel.com/docs/concepts/deployments/environments#production" target="_blank" rel="noopener noreferrer">\uD504\uB85C\uB355\uC158 \uBC30\uD3EC</a>\uAC00 \uB429\uB2C8\uB2E4.</p><p>\uC774\uC5D0 \uB300\uD574 \uC880 \uB354 \uC54C\uACE0 \uC2F6\uB2E4\uBA74 Vercel\uC758 <a href="https://vercel.com/docs/concepts/git" target="_blank" rel="noopener noreferrer">Git</a> \uBB38\uC11C\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><h2 id="azure-static-web-apps" tabindex="-1">Azure Static Web Apps <a class="header-anchor" href="#azure-static-web-apps" aria-hidden="true">#</a></h2><p>You can quickly deploy your Vite app with Microsoft Azure <a href="https://aka.ms/staticwebapps" target="_blank" rel="noopener noreferrer">Static Web Apps</a> service. You need:</p><p>\uB9C8\uC774\uD06C\uB85C\uC18C\uD504\uD2B8 Azure \uD074\uB77C\uC6B0\uB4DC \uC11C\uBE44\uC2A4\uC758 <a href="https://aka.ms/staticwebapps" target="_blank" rel="noopener noreferrer">Static Web Apps</a> \uC11C\uBE44\uC2A4\uB97C \uC774\uC6A9\uD574 \uBE60\uB974\uAC8C Vite \uC571\uC744 \uBC30\uD3EC\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li>Azure \uACC4\uC815\uACFC \uAD6C\uB3C5(Subscription) \uD0A4\uAC00 \uD544\uC694\uD574\uC694. <a href="https://azure.microsoft.com/free" target="_blank" rel="noopener noreferrer">\uC5EC\uAE30\uC11C \uBB34\uB8CC\uB85C Azure \uACC4\uC815\uC744 \uB9CC\uB4E4 \uC218 \uC788\uB2F5\uB2C8\uB2E4</a>.</li><li>Vite \uC571\uC744 <a href="https://github.com" target="_blank" rel="noopener noreferrer">GitHub</a>\uC5D0 Push \uD574\uC8FC\uC138\uC694.</li><li><a href="https://code.visualstudio.com" target="_blank" rel="noopener noreferrer">Visual Studio Code</a>\uC758 <a href="https://marketplace.visualstudio.com/items?itemName=ms-azuretools.vscode-azurestaticwebapps" target="_blank" rel="noopener noreferrer">SWA(Static Web Apps) \uD655\uC7A5 \uD504\uB85C\uADF8\uB7A8</a>\uC744 \uC124\uCE58\uD574\uC8FC\uC138\uC694.</li></ul><p>VS Code\uC5D0 \uD655\uC7A5 \uD504\uB85C\uADF8\uB7A8\uC744 \uC124\uCE58\uD55C \uB4A4 \uC571\uC758 \uB8E8\uD2B8 \uB514\uB809\uD130\uB9AC\uB85C \uB4E4\uC5B4\uAC00\uC8FC\uC138\uC694. \uC774\uD6C4 \uC124\uCE58\uD55C SWA \uD655\uC7A5 \uD504\uB85C\uADF8\uB7A8\uC744 \uC2E4\uD589\uD558\uACE0, Azure\uC5D0 \uB85C\uADF8\uC778 \uD55C \uB4A4, &#39;+&#39; \uBC84\uD2BC\uC744 \uB20C\uB7EC \uC0C8\uB85C\uC6B4 \uC815\uC801 \uC6F9 \uC571(Static Web App)\uC744 \uB9CC\uB4E4\uC5B4\uC8FC\uC138\uC694. \uC5EC\uAE30\uC11C \uC55E\uC11C \uC0DD\uC131\uD55C \uAD6C\uB3C5 \uD0A4\uB97C \uC124\uC815\uD558\uB77C\uB294 \uBA54\uC2DC\uC9C0\uAC00 \uB098\uC624\uAC8C \uB429\uB2C8\uB2E4.</p><p>\uD655\uC7A5 \uD504\uB85C\uADF8\uB7A8\uC744 \uD1B5\uD574 \uC2DC\uC791\uB41C \uB9C8\uBC95\uC0AC\uB97C \uB530\uB77C \uC571 \uC774\uB984\uC744 \uC9C0\uC815\uD558\uACE0, \uD504\uB808\uC784\uC6CC\uD06C\uC5D0 \uB300\uD55C \uC0AC\uC804 \uC124\uC815\uC744 \uC120\uD0DD\uD55C \uB4A4, \uC571 \uB8E8\uD2B8(\uC77C\uBC18\uC801\uC73C\uB85C <code>/</code>) \uBC0F \uBE4C\uB4DC\uB41C \uD30C\uC77C \uC704\uCE58(<code>/dist</code>)\uB97C \uC9C0\uC815\uD574\uC8FC\uC138\uC694. \uB9C8\uBC95\uC0AC\uAC00 \uC2E4\uD589\uB418\uBA70 <code>.github</code> \uD3F4\uB354\uC758 \uC800\uC7A5\uC18C\uC5D0 GitHub Actions\uAC00 \uAD6C\uC131\uB429\uB2C8\uB2E4.</p><p>\uC0C8\uB85C\uC774 \uAD6C\uC131\uB41C GitHub Action\uC740 \uC571 \uBC30\uD3EC \uC2DC \uB3D9\uC791\uD558\uBA70(GitHub\uC758 Actions \uD0ED\uC5D0\uC11C \uD655\uC778\uD560 \uC218 \uC788\uC5B4\uC694), \uC131\uACF5\uC801\uC73C\uB85C \uC644\uB8CC\uB418\uBA74 \uD655\uC7A5 \uD504\uB85C\uADF8\uB7A8\uC758 \uC9C4\uD589\uB960 \uCC3D\uC5D0\uC11C \uC81C\uACF5\uB418\uB294 &#39;Browse Website&#39; \uBC84\uD2BC\uC73C\uB85C \uBC30\uD3EC\uB41C \uC571\uC744 \uBCFC \uC218 \uC788\uAC8C \uB429\uB2C8\uB2E4.</p>`,51),p=[o];function c(l,r,i,u,d,k){return n(),a("div",null,p)}var b=e(t,[["render",c]]);export{g as __pageData,b as default};
