import{_ as n,c as a,o as s,a as e}from"./app.18df0fee.js";const f='{"title":"\uD50C\uB7EC\uADF8\uC778 \uC0AC\uC6A9\uD558\uAE30","description":"","frontmatter":{"title":"\uD50C\uB7EC\uADF8\uC778 \uC0AC\uC6A9\uD558\uAE30"},"headers":[{"level":2,"title":"\uD50C\uB7EC\uADF8\uC778 \uCD94\uAC00\uD558\uAE30","slug":"adding-a-plugin"},{"level":2,"title":"\uD50C\uB7EC\uADF8\uC778\uC744 \uCC3E\uB294 \uBC29\uBC95","slug":"finding-plugins"},{"level":2,"title":"\uD50C\uB7EC\uADF8\uC778 \uC21C\uC11C \uC815\uD558\uAE30","slug":"enforcing-plugin-ordering"},{"level":2,"title":"\uC870\uAC74\uBD80 \uD50C\uB7EC\uADF8\uC778","slug":"conditional-application"},{"level":2,"title":"\uD50C\uB7EC\uADF8\uC778 \uC791\uC131","slug":"building-plugins"}],"relativePath":"guide/using-plugins.md"}',p={},t=e(`<h1 id="using-plugins" tabindex="-1">\uD50C\uB7EC\uADF8\uC778 \uC0AC\uC6A9\uD558\uAE30 <a class="header-anchor" href="#using-plugins" aria-hidden="true">#</a></h1><p>Vite\uB294 \uBA87 \uAC00\uC9C0 \uCD94\uAC00\uC801\uC778 Vite \uC804\uC6A9 \uC635\uC158\uACFC \uD568\uAED8 \uC798 \uC124\uACC4\uB41C Rollup\uC758 \uD50C\uB7EC\uADF8\uC778 \uC778\uD130\uD398\uC774\uC2A4\uB97C \uAE30\uBC18\uC73C\uB85C \uD558\uB294 \uD50C\uB7EC\uADF8\uC778\uB4E4\uC744 \uC0AC\uC6A9\uD558\uC5EC \uD655\uC7A5\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 Vite \uC0AC\uC6A9\uC790\uAC00 \uD544\uC694\uC5D0 \uB530\uB77C \uAC1C\uBC1C \uC11C\uBC84\uB098 SSR\uACFC \uAC19\uC740 \uAE30\uB2A5\uB4E4\uC744 \uD655\uC7A5\uD560 \uC218 \uC788\uB294 \uAC83\uACFC \uB3D9\uC2DC\uC5D0, \uAC80\uC99D\uB41C Rollup \uD50C\uB7EC\uADF8\uC778 \uC0DD\uD0DC\uACC4\uC5D0\uB3C4 \uC758\uC874\uD560 \uC218 \uC788\uC74C\uC744 \uC758\uBBF8\uD569\uB2C8\uB2E4.</p><h2 id="adding-a-plugin" tabindex="-1">\uD50C\uB7EC\uADF8\uC778 \uCD94\uAC00\uD558\uAE30 <a class="header-anchor" href="#adding-a-plugin" aria-hidden="true">#</a></h2><p>\uD50C\uB7EC\uADF8\uC778\uC744 \uC0AC\uC6A9\uD558\uB824\uBA74 \uD504\uB85C\uC81D\uD2B8\uC758 <code>devDependencies</code>\uC5D0 \uD50C\uB7EC\uADF8\uC778\uC744 \uCD94\uAC00\uD558\uACE0, <code>vite.config.js</code> \uC124\uC815 \uD30C\uC77C\uC758 <code>plugins</code> \uBC30\uC5F4\uC5D0 \uD574\uB2F9 \uD50C\uB7EC\uADF8\uC778\uC744 \uD3EC\uD568\uC2DC\uCF1C\uC57C \uD569\uB2C8\uB2E4. \uC608\uB97C \uB4E4\uC5B4, \uB808\uAC70\uC2DC \uD504\uB77C\uC6B0\uC800\uC5D0 \uB300\uD55C \uC9C0\uC6D0\uC744 \uC81C\uACF5\uD558\uAE30 \uC704\uD574 \uACF5\uC2DD \uD50C\uB7EC\uADF8\uC778 \uC911 \uD558\uB098\uC778 <a href="https://github.com/vitejs/vite/tree/main/packages/plugin-legacy" target="_blank" rel="noopener noreferrer">@vitejs/plugin-legacy</a>\uB97C \uC0AC\uC6A9\uD558\uACE0\uC790 \uD55C\uB2E4\uBA74 \uB2E4\uC74C\uACFC \uAC19\uC774 \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4:</p><div class="language-"><pre><code>$ npm add -D @vitejs/plugin-legacy
</code></pre></div><div class="language-js"><pre><code><span class="token comment">// vite.config.js</span>
<span class="token keyword">import</span> legacy <span class="token keyword">from</span> <span class="token string">&#39;@vitejs/plugin-legacy&#39;</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> defineConfig <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vite&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token function">defineConfig</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">plugins</span><span class="token operator">:</span> <span class="token punctuation">[</span>
    <span class="token function">legacy</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
      <span class="token literal-property property">targets</span><span class="token operator">:</span> <span class="token punctuation">[</span><span class="token string">&#39;defaults&#39;</span><span class="token punctuation">,</span> <span class="token string">&#39;not IE 11&#39;</span><span class="token punctuation">]</span>
    <span class="token punctuation">}</span><span class="token punctuation">)</span>
  <span class="token punctuation">]</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p><code>plugins</code>\uC5D0\uB294 \uC5EC\uB7EC \uD50C\uB7EC\uADF8\uC778\uC744 \uD558\uB098\uC758 \uC694\uC18C\uB85C \uBB36\uB294 \uC0AC\uC804 \uC124\uC815\uB3C4 \uD3EC\uD568\uC2DC\uD0AC \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 \uD504\uB808\uC784\uC6CC\uD06C \uD1B5\uD569\uACFC \uAC19\uC740 \uC5EC\uB7EC \uD50C\uB7EC\uADF8\uC778\uC744 \uC0AC\uC6A9\uD558\uC5EC \uAD6C\uD604\uB418\uB294 \uBCF5\uC7A1\uD55C \uAE30\uB2A5\uC744 \uAD6C\uD604\uD560 \uB54C \uC720\uC6A9\uD558\uAC8C \uC0AC\uC6A9\uB420 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774 \uB54C \uC804\uB2EC\uB418\uB294 \uBC30\uC5F4\uC740 \uB0B4\uBD80\uC801\uC73C\uB85C Flatten \uC5F0\uC0B0\uC744 \uAC70\uCE58\uAC8C \uB429\uB2C8\uB2E4.</p><p>\uCC38\uACE0\uB85C \uB2E4\uB978 \uD50C\uB7EC\uADF8\uC778\uC744 \uC27D\uAC8C \uD65C\uC131\uD654\uD558\uAC70\uB098 \uBE44\uD65C\uC131\uD654 \uC2DC\uD0AC \uC218 \uC788\uB294 \uC798\uBABB\uB41C \uD50C\uB7EC\uADF8\uC778\uC740 \uBB34\uC2DC\uB429\uB2C8\uB2E4.</p><h2 id="finding-plugins" tabindex="-1">\uD50C\uB7EC\uADF8\uC778\uC744 \uCC3E\uB294 \uBC29\uBC95 <a class="header-anchor" href="#finding-plugins" aria-hidden="true">#</a></h2><div class="tip custom-block"><p class="custom-block-title">\uCC38\uACE0</p><p>Vite\uB294 \uC6F9 \uAC1C\uBC1C \uC2DC \uC77C\uBC18\uC801\uC73C\uB85C \uD544\uC694\uD55C \uB300\uBD80\uBD84\uC758 \uAE30\uB2A5\uB4E4\uC744 \uC81C\uACF5\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uB530\uB77C\uC11C Vite \uB610\uB294 \uD638\uD658\uB418\uB294 Rollup \uD50C\uB7EC\uADF8\uC778\uC744 \uAC80\uC0C9\uD558\uAE30 \uC804, <a href="./../guide/features.html">\uC9C0\uC6D0\uD558\uB294 \uAE30\uB2A5\uB4E4</a> \uC139\uC158\uC744 \uBA3C\uC800 \uD655\uC778\uD574\uC8FC\uC138\uC694. Rollup\uC73C\uB85C \uD504\uB85C\uC81D\uD2B8\uB97C \uAD6C\uC131\uD560 \uB54C \uD544\uC694\uD588\uB358 \uD50C\uB7EC\uADF8\uC778 \uB300\uBD80\uBD84\uC740 \uC774\uBBF8 Vite\uC5D0\uC11C \uC790\uCCB4\uC801\uC73C\uB85C \uC81C\uACF5\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4.</p></div><p>\uACF5\uC2DD \uD50C\uB7EC\uADF8\uC778\uC5D0 \uB300\uD55C \uC815\uBCF4\uB294 <a href="./../plugins/">\uD50C\uB7EC\uADF8\uC778 \uC139\uC158</a>\uC744 \uCC38\uACE0\uD574\uC8FC\uC138\uC694. Vite \uCEE4\uBBA4\uB2C8\uD2F0 \uD50C\uB7EC\uADF8\uC778\uC740 <a href="https://github.com/vitejs/awesome-vite#plugins" target="_blank" rel="noopener noreferrer">awesome-vite</a>\uC5D0\uC11C \uBCFC \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uD638\uD658\uB418\uB294 Rollup \uD50C\uB7EC\uADF8\uC778\uC758 \uACBD\uC6B0 <a href="https://vite-rollup-plugins.patak.dev" target="_blank" rel="noopener noreferrer">Vite Rollup Plugins</a>\uC5D0\uC11C \uC0AC\uC6A9\uBC95\uACFC \uD568\uAED8 \uC81C\uACF5\uB418\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uB9CC\uC57D \uC774 \uBAA9\uB85D\uC5D0 \uC5C6\uB2E4\uBA74 <a href="./../guide/api-plugin.html#rollup-plugin-compatibility">Rollup \uD50C\uB7EC\uADF8\uC778 \uD638\uD658\uC131 \uC139\uC158</a>\uC744 \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p><p>\uB610\uD55C NPM\uC5D0 Vite \uBC0F Rollup \uD50C\uB7EC\uADF8\uC778\uC744 \uAC8C\uC2DC\uD560 \uB54C <a href="./api-plugin.html#conventions">\uCEE8\uBCA4\uC158</a>\uC744 \uB530\uB974\uB3C4\uB85D \uAD8C\uACE0\uD558\uACE0 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB97C \uC774\uC6A9\uD574 \uC5B4\uB835\uC9C0 \uC54A\uAC8C NPM\uC5D0\uC11C Vite \uBC0F Rollup \uD50C\uB7EC\uADF8\uC778\uC744 \uAC80\uC0C9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><ul><li><a href="https://www.npmjs.com/search?q=vite-plugin&amp;ranking=popularity" target="_blank" rel="noopener noreferrer">Vite \uD50C\uB7EC\uADF8\uC778 \uAC80\uC0C9 \uACB0\uACFC</a></li><li><a href="https://www.npmjs.com/search?q=rollup-plugin&amp;ranking=popularity" target="_blank" rel="noopener noreferrer">Rollup \uD50C\uB7EC\uADF8\uC778 \uAC80\uC0C9 \uACB0\uACFC</a></li></ul><h2 id="enforcing-plugin-ordering" tabindex="-1">\uD50C\uB7EC\uADF8\uC778 \uC21C\uC11C \uC815\uD558\uAE30 <a class="header-anchor" href="#enforcing-plugin-ordering" aria-hidden="true">#</a></h2><p>\uC77C\uBD80 Rollup \uD50C\uB7EC\uADF8\uC778\uACFC\uC758 \uD638\uD658\uC744 \uC704\uD574 \uD50C\uB7EC\uADF8\uC778 \uC21C\uC11C\uB97C \uC815\uD558\uAC70\uB098 \uBE4C\uB4DC \uC2DC\uC5D0\uB9CC \uD50C\uB7EC\uADF8\uC778\uC774 \uB3D9\uC791\uD558\uB3C4\uB85D \uAD6C\uC131\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4. \uC774\uB294 Vite \uD50C\uB7EC\uADF8\uC778\uB9CC\uC744 \uC704\uD55C \uAE30\uB2A5\uC774\uBA70, \uAC00\uB839 \uD50C\uB7EC\uADF8\uC778 \uC21C\uC11C\uB97C \uC815\uD558\uACE0\uC790 \uD558\uB294 \uACBD\uC6B0 <code>enforce</code> \uD504\uB85C\uD37C\uD2F0\uB97C \uC774\uC6A9\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4:</p><ul><li><code>pre</code>: Vite\uC758 \uCF54\uC5B4 \uD50C\uB7EC\uADF8\uC778\uBCF4\uB2E4 \uBA3C\uC800 \uC2E4\uD589\uD558\uACE0\uC790 \uD558\uB294 \uD50C\uB7EC\uADF8\uC778</li><li>default: Vite\uC758 \uCF54\uC5B4 \uD50C\uB7EC\uADF8\uC778 \uC774\uD6C4\uC5D0 \uC2E4\uD589\uD558\uACE0\uC790 \uD558\uB294 \uD50C\uB7EC\uADF8\uC778</li><li><code>post</code>: Vite\uC758 \uBE4C\uB4DC \uD50C\uB7EC\uADF8\uC778 \uC774\uD6C4\uC5D0 \uC2E4\uD589\uD558\uACE0\uC790 \uD558\uB294 \uD50C\uB7EC\uADF8\uC778</li></ul><div class="language-js"><pre><code><span class="token comment">// vite.config.js</span>
<span class="token keyword">import</span> image <span class="token keyword">from</span> <span class="token string">&#39;@rollup/plugin-image&#39;</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> defineConfig <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vite&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token function">defineConfig</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">plugins</span><span class="token operator">:</span> <span class="token punctuation">[</span>
    <span class="token punctuation">{</span>
      <span class="token operator">...</span><span class="token function">image</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
      <span class="token literal-property property">enforce</span><span class="token operator">:</span> <span class="token string">&#39;pre&#39;</span>
    <span class="token punctuation">}</span>
  <span class="token punctuation">]</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><p>\uC774\uC5D0 \uB300\uD574 \uC880 \uB354 \uC790\uC138\uD55C \uB0B4\uC6A9\uC740 <a href="./api-plugin.html#plugin-ordering">\uD50C\uB7EC\uADF8\uC778 API \uAC00\uC774\uB4DC</a>\uB97C \uD655\uC778\uD574\uC8FC\uC138\uC694. <a href="https://vite-rollup-plugins.patak.dev" target="_blank" rel="noopener noreferrer">Vite Rollup Plugins</a> \uBAA9\uB85D\uC5D0\uC11C\uB3C4 <code>enforce</code> \uB808\uC774\uBE14\uACFC \uC0AC\uC6A9 \uC9C0\uCE68\uC744 \uD1B5\uD574 \uC774\uC640 \uAD00\uB828\uB41C \uB0B4\uC6A9\uC744 \uD655\uC778\uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4.</p><h2 id="conditional-application" tabindex="-1">\uC870\uAC74\uBD80 \uD50C\uB7EC\uADF8\uC778 <a class="header-anchor" href="#conditional-application" aria-hidden="true">#</a></h2><p>\uAE30\uBCF8\uC801\uC73C\uB85C \uD50C\uB7EC\uADF8\uC778\uC740 \uAC1C\uBC1C \uC11C\uBC84(<code>&#39;serve&#39;</code>)\uC640 \uBE4C\uB4DC(<code>&#39;build&#39;</code>) \uC2DC \uBAA8\uB450 \uB3D9\uC791\uD569\uB2C8\uB2E4. \uB9CC\uC57D \uC870\uAC74\uBD80\uB85C \uB3D9\uC791\uD558\uAE30\uB97C \uC6D0\uD55C\uB2E4\uBA74, <code>apply</code> \uD504\uB85C\uD37C\uD2F0\uB97C \uC774\uC6A9\uD574 <code>&#39;build&#39;</code> \uB610\uB294 <code>&#39;serve&#39;</code> \uC911\uC5D0\uB9CC \uD50C\uB7EC\uADF8\uC778\uC774 \uB3D9\uC791\uD558\uB3C4\uB85D \uD560 \uC218 \uC788\uC2B5\uB2C8\uB2E4:</p><div class="language-js"><pre><code><span class="token comment">// vite.config.js</span>
<span class="token keyword">import</span> typescript2 <span class="token keyword">from</span> <span class="token string">&#39;rollup-plugin-typescript2&#39;</span>
<span class="token keyword">import</span> <span class="token punctuation">{</span> defineConfig <span class="token punctuation">}</span> <span class="token keyword">from</span> <span class="token string">&#39;vite&#39;</span>

<span class="token keyword">export</span> <span class="token keyword">default</span> <span class="token function">defineConfig</span><span class="token punctuation">(</span><span class="token punctuation">{</span>
  <span class="token literal-property property">plugins</span><span class="token operator">:</span> <span class="token punctuation">[</span>
    <span class="token punctuation">{</span>
      <span class="token operator">...</span><span class="token function">typescript2</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">,</span>
      <span class="token literal-property property">apply</span><span class="token operator">:</span> <span class="token string">&#39;build&#39;</span>
    <span class="token punctuation">}</span>
  <span class="token punctuation">]</span>
<span class="token punctuation">}</span><span class="token punctuation">)</span>
</code></pre></div><h2 id="building-plugins" tabindex="-1">\uD50C\uB7EC\uADF8\uC778 \uC791\uC131 <a class="header-anchor" href="#building-plugins" aria-hidden="true">#</a></h2><p>\uD50C\uB7EC\uADF8\uC778 \uC791\uC131\uC5D0 \uB300\uD55C \uBB38\uC11C\uB294 <a href="./api-plugin.html">\uD50C\uB7EC\uADF8\uC778 API \uAC00\uC774\uB4DC</a>\uB97C \uCC38\uACE0\uD574\uC8FC\uC138\uC694.</p>`,23),o=[t];function i(l,c,r,u,d,k){return s(),a("div",null,o)}var h=n(p,[["render",i]]);export{f as __pageData,h as default};
